INSERT INTO `fa_admin_log` VALUES (140, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"21\",\"solar_time\":\"2022-09-13 22:42:25\",\"lunar_time\":\"1837-03-02 00:00:00\",\"switch\":\"0\",\"content\":\"两江总督陶澍到江西阅兵，顺便请假回原籍扫墓，道经醴陵，住在行馆中，看到宗棠的对联，约见订交而别。他得知宗棠次年将赴京会试，临别时再三嘱咐：“会试毕，不论中与不中，务必绕道南京，来督署衙门盘桓几天。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663080284);
INSERT INTO `fa_admin_log` VALUES (141, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-13 22:45:54\",\"lunar_time\":\"1837-12-01 00:00:00\",\"switch\":\"0\",\"content\":\"两江总督\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663080387);
INSERT INTO `fa_admin_log` VALUES (142, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-13 22:47:10\",\"lunar_time\":\"1838-01-01 00:00:00\",\"switch\":\"0\",\"content\":\"第三次（最后一次）会试，曾住在周振之家，会试落第，绕道南京，拜会陶澍，左宗棠下定决心，以后不再参加科举考试了。他又注意起农业，开了试验田，写了农书，还准备做一辈子的农人。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663080609);
INSERT INTO `fa_admin_log` VALUES (143, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-13 22:50:11\",\"lunar_time\":\"1838-03-01 00:00:00\",\"switch\":\"0\",\"content\":\"写过一篇《广区田图说》，极力推广古代分区种田的区田制，他认为有六善三便，可以节水、省肥、积水保墒、防虫害、省劳力、亩产高等\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663080627);
INSERT INTO `fa_admin_log` VALUES (144, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-13 22:52:04\",\"lunar_time\":\"1839-06-01 00:00:00\",\"switch\":\"0\",\"content\":\"夏天左宗棠来到长沙，又和左宗植相会。识邓显鹤、邹汉勋。钻研地理及历代兵事。于湘潭所居种桑千本，令家人饲蚕、治丝。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663080764);
INSERT INTO `fa_admin_log` VALUES (145, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-13 22:52:45\",\"lunar_time\":\"1839-09-01 00:00:00\",\"switch\":\"0\",\"content\":\"贺熙龄准备回北京。他的朋友和学生共 10 人会集在长沙城南，饮酒话别，在座的除左宗棠、邓显鹤、罗研生、邹汉勋等人外，左宗植也来了。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663080848);
INSERT INTO `fa_admin_log` VALUES (146, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"20\",\"solar_time\":\"2022-09-13 22:54:20\",\"lunar_time\":\"1839-09-01 00:00:00\",\"switch\":\"0\",\"content\":\"回北京，特别思念自己最得意的学生左宗棠，就写一首\\r\\n《舟中怀左季高诗》：\\r\\n六朝花月毫端扫，\\r\\n万里江山眼底横。\\r\\n开口能谈天下事，\\r\\n读书深抱古人情。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663080899);
INSERT INTO `fa_admin_log` VALUES (147, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"21\",\"solar_time\":\"\",\"lunar_time\":\"1839-09-01 00:00:00\",\"switch\":\"0\",\"content\":\"陶澍死于南京任所，贺熙龄写信给左宗棠，要他去安化陶家教孤子陶桄读书。宗棠于是遵命到安化小淹陶家坐馆，陶家藏书很多，对他后来的事业有很大的帮助。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663081012);
