INSERT INTO `fa_admin_log` VALUES (152, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-14 09:55:55\",\"lunar_time\":\"1841-01-01 00:00:00\",\"switch\":\"0\",\"content\":\"英军占据香港，进犯浙江,他还抱希望于林则徐，认为只有林则徐复出，收拾局面，还能固守东南半壁。但他也慨叹：“恐怕逋翁是出不来了。他只有慨叹 “国家前途茫茫”，也渐渐由积极变为消极\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663120632);
