INSERT INTO `fa_admin_log` VALUES (164, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-14 15:23:05\",\"lunar_time\":\"1844-01-01 00:00:00\",\"switch\":\"0\",\"content\":\"左宗棠那时大部分时间还在安化，每次回到柳庄，他就专心钻研农事。除了水稻外，还种茶、桑、竹和其他树木，也养蚕、种菜、种花等。他雇用了少数有农业经验的长工师傅，农忙时还雇用短工帮忙。成功后，推广到农民中去，让广大农民都获得利益。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663140239);
INSERT INTO `fa_admin_log` VALUES (165, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-14 15:24:18\",\"lunar_time\":\"1844-01-01 00:00:00\",\"switch\":\"0\",\"content\":\"左宗棠曾去长沙，见到老师贺熙龄#20#。当他回到小淹时，才知道陶澍#21#夫人已病逝。翌年胡林翼#18#来吊岳母之丧，在小淹住了 10 天\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663140320);
INSERT INTO `fa_admin_log` VALUES (166, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-14 15:25:36\",\"lunar_time\":\"1845-01-01 00:00:00\",\"switch\":\"0\",\"content\":\"写了一部《朴存阁农书》，“朴存” 是他自己起的别号，书共十余卷，分门别类叙述各种农事\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663140366);
INSERT INTO `fa_admin_log` VALUES (167, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-14 15:26:26\",\"lunar_time\":\"1846-09-01 00:00:00\",\"switch\":\"0\",\"content\":\"老师熙龄#20#去世，那年八月宗棠的长子孝威出生。熙龄知道了，非常高兴，说：“这孩子该做我的女婿。”\\r\\n贺熙龄死后，左宗棠写了一副挽联：\\r\\n宋儒学，汉人文，落落几知心，公自有书遗后世；\\r\\n定王台，贾傅井，行行重回首，我从何处哭先生！\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663140449);
INSERT INTO `fa_admin_log` VALUES (168, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"9\",\"solar_time\":\"2022-09-14 15:28:49\",\"lunar_time\":\"2022-09-14 15:28:49\",\"switch\":\"0\",\"content\":\"孝宽出生，周夫人#3#体弱，没有奶水，两个男孩都由张夫人哺乳。每次她都先将孝威#10#喂饱了，然后再喂孝宽#11#\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663140604);
