INSERT INTO `fa_admin_log` VALUES (205, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-14 22:31:48\",\"lunar_time\":\"1854-02-01 00:00:00\",\"switch\":\"0\",\"content\":\"公居白水洞，太平军占岳州、湘阴，扬言将入山找左宗棠。曾国藩治军衡州，贻书邀公，公辞之。骆秉章复遣使入山敦促，公念时事益棘，乃再度入湘幕。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663165995);
