INSERT INTO `fa_admin_log` VALUES (212, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"27\",\"solar_time\":\"2022-09-15 11:15:18\",\"lunar_time\":\"1854-11-01 00:00:00\",\"switch\":\"0\",\"content\":\"水陆各师进薄九江。曾国藩十分得意，湘军军威大振，却露出了骄气。左宗棠觉察到湘军虽然节节得手，但 “将士之气渐骄，主帅之谋渐乱”，败象已露。便连写数信给曾国藩，劝他持军务必须慎重，不宜轻进。但曾国藩正在兴头之上，不予答复。终于于十二月率兵进攻湖口。大败于太平军损失惨重\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663211749);
