INSERT INTO `fa_admin_log` VALUES (249, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-15 16:07:19\",\"lunar_time\":\"1859-05-01 00:00:00\",\"switch\":\"0\",\"content\":\"五六月间，湖南重镇宝庆（今邵阳）被石达开三十万大军重重包围。左宗棠得到围城守军送出来的一份 “敌我兵势地图”，他将图挂在墙上，早晚详细研究。左宗棠从兵势分析，认为东面敌人兵力雄厚，北面薄弱，应从北面进攻。争论了两天，最后采取了左宗棠的意见。湘军由宝庆北面的新化进兵，获得大胜。石达开军败退到东安，由东安又进入广西兴安，湖南于是再度解围。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663229284);
