INSERT INTO `fa_admin_log` VALUES (251, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"25\",\"solar_time\":\"2022-09-15 16:10:36\",\"lunar_time\":\"1858-03-01 00:00:00\",\"switch\":\"0\",\"content\":\"骆秉章到北京见皇帝时，告了樊燮一状，后来查实参奏，奉旨将樊燮革职查办。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663229517);
INSERT INTO `fa_admin_log` VALUES (252, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-15 16:11:59\",\"lunar_time\":\"2022-09-15 16:11:59\",\"switch\":\"0\",\"content\":\"樊燮事件,湖广总督总督官文是骆秉章顶头上司，过去二人有矛盾，因此想借樊燮告状的机会，压一压汉人的气焰。\\r\\n因此官文接受了樊燮控告，立即奏报朝廷，说是永州知府黄文琛等与湖南巡抚幕客劣绅左宗棠，商同陷害总兵樊燮。就下旨将本案交官文和湖北正考官钱宝青审办，密旨中说：“如果左宗棠确有不法情事，可即就地正法。”\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663230155);
