INSERT INTO `fa_admin_log` VALUES (254, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"31\",\"solar_time\":\"2022-09-15 16:25:52\",\"lunar_time\":\"1859-12-02 00:00:00\",\"switch\":\"0\",\"content\":\"郭嵩焘赶忙找了和他同在南书房值班的少詹潘祖荫，请他给皇上保荐奏疏，并对潘祖荫说：“如果把左宗棠搞走了，湖南就会垮台，东南大局也就完了。”，潘祖荫于是给皇帝上了一疏，说：“湖南军队不仅保住了本省，还支援了江西、湖北、广西、贵州，所向克捷。固然是骆秉章调度有方，实则由左宗棠运筹决胜。此天下所共见，也在皇上圣明洞鉴之中。” 接着他说了两句传诵一时的话：“国家不可一日无湖南，湖南不可一日无左宗棠。” 咸丰帝采纳了他的意见。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663230438);
