INSERT INTO `fa_admin_log` VALUES (260, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-15 16:32:59\",\"lunar_time\":\"1860-03-26 00:00:00\",\"switch\":\"0\",\"content\":\"到宿松,曾国藩，胡林翼，李鸿章、李瀚章（字筱泉）、李次青（名元度）、曾国荃等也都来到宿松，他们在一起畅谈天下大势，尤以曾、左、胡三人在一处聚谈最多，往往从早饭后一直谈到深夜。这次宿松会晤十分重要，关系到湘军今后发展动向，自然也关系他们个人和国家的前途，以及清朝的命运。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663230821);
INSERT INTO `fa_admin_log` VALUES (261, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/136?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1860-01-23 00:00:00\",\"lunar_time\":\"1860-01-01 00:00:00\",\"content\":\"他向骆秉章提出要参加会试，以为摆脱幕府的借口。自长沙启行赴京参加第四次会试。左宗棠的名气已很大，皇帝也点过他几次名。京师的考官们都互相告诫，“这次务必要取中左宗棠”\"},\"ids\":\"136\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663230870);
INSERT INTO `fa_admin_log` VALUES (262, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/139?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1860-04-16 00:00:00\",\"lunar_time\":\"1860-03-26 00:00:00\",\"content\":\"到宿松,曾国藩，胡林翼，李鸿章、李瀚章（字筱泉）、李次青（名元度）、曾国荃等也都来到宿松，他们在一起畅谈天下大势，尤以曾、左、胡三人在一处聚谈最多，往往从早饭后一直谈到深夜。这次宿松会晤十分重要，关系到湘军今后发展动向，自然也关系他们个人和国家的前途，以及清朝的命运。\\r\\n因潘祖荫、郭嵩焘、肃顺极力疏救，官文、樊燮构陷事解。又得曾国藩、胡林翼保奏，奉旨以四品京堂候补，随曾国藩襄办军务。\"},\"ids\":\"139\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663231174);
INSERT INTO `fa_admin_log` VALUES (263, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"28\",\"solar_time\":\"2022-09-15 16:39:56\",\"lunar_time\":\"1860-03-01 00:00:00\",\"switch\":\"0\",\"content\":\"击破江南大营，歼敌数万人，清军主帅和春自杀。太平军重振了威势，连克苏州、常州，进入浙江，攻陷嘉兴。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663231252);
INSERT INTO `fa_admin_log` VALUES (264, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/139?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1860-04-16 00:00:00\",\"lunar_time\":\"1860-03-26 00:00:00\",\"content\":\"到宿松,曾国藩，胡林翼，李鸿章、李瀚章（字筱泉）、李次青（名元度）、曾国荃等也都来到宿松，他们在一起畅谈天下大势，尤以曾、左、胡三人在一处聚谈最多，往往从早饭后一直谈到深夜。这次宿松会晤十分重要，关系到湘军今后发展动向，自然也关系他们个人和国家的前途，以及清朝的命运。\"},\"ids\":\"139\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663231260);
INSERT INTO `fa_admin_log` VALUES (265, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-15 16:41:02\",\"lunar_time\":\"1860-05-01 00:00:00\",\"switch\":\"0\",\"content\":\"接奉诏书，以四品京堂候补，随曾国藩襄办军务。他随即在长沙招募了五千人，成立了楚军。楚军总共 5000 余人，从此，他就打消了隐居山林的念头，告别了家乡，率领楚军东征西讨，开始了几十年的戎马生涯。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663232661);
INSERT INTO `fa_admin_log` VALUES (266, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-15 17:04:54\",\"lunar_time\":\"1860-07-01 00:00:00\",\"switch\":\"0\",\"content\":\"楚军东征,从长沙出发，经醴陵进入江西，原想去祁门与曾国藩军会合。半路上听到太平军已占领徽州，于是左宗棠率部队由南昌经过乐平，进驻江西东北门户景德镇。太平军别部由广东进入赣南，准备进占景德镇。左宗棠派王开琳的老湘营击退了它。这支别部就逃往景德镇东南的德兴，左宗棠派王开化、杨昌濬率部队在半路截击，王开琳的老湘营又从后追来，太平军大败而逃，楚军乘胜收复了德兴，，两天后收复了婺源，太平军逃往浙江。楚军出师首战告捷，收复两要地，在江西站稳了脚跟。这也是左宗棠的第一次实战。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663232771);
INSERT INTO `fa_admin_log` VALUES (267, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/119?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-10-10 00:00:00\",\"lunar_time\":\"1856-06-01 00:00:00\",\"content\":\"朝廷命赐以兵部郎中衔\"},\"ids\":\"119\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663234831);
INSERT INTO `fa_admin_log` VALUES (268, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/121?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1856-10-23 00:00:00\",\"lunar_time\":\"1856-09-25 00:00:00\",\"content\":\"第二次鸦片战争开始,英军开始行动，三天之内，连占虎门口内各炮台。27日，英舰炮轰广州城。29日，英军攻入城内，抢掠广州督署后退出。\"},\"ids\":\"121\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663234885);
INSERT INTO `fa_admin_log` VALUES (269, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1856-12-01 00:00:00\",\"lunar_time\":\"\",\"switch\":\"0\",\"content\":\"第二次鸦片战争:洋行夷馆被毁，尽成灰烬。一艘自广州开往香港的英国邮船遭劫。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663234929);
