INSERT INTO `fa_admin_log` VALUES (271, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/127?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1858-06-13 00:00:00\",\"lunar_time\":\"\",\"content\":\"英法军舰攻占大沽炮台，强迫签订中英、中法《天津条约》。\"},\"ids\":\"127\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663235231);
INSERT INTO `fa_admin_log` VALUES (272, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1859-06-01 00:00:00\",\"lunar_time\":\"\",\"switch\":\"0\",\"content\":\"英、法政府远不满足从《天津条约》攫取的种种特权，蓄意利用换约之机挑起第二次大沽口之战\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663235329);
