INSERT INTO `fa_admin_log` VALUES (275, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1860-04-01 00:00:00\",\"lunar_time\":\"\",\"switch\":\"0\",\"content\":\"英、法帝国主义当局分别再度任命额尔金和葛罗为全权代表，率领英军一万五千余人，法军约七千人，扩大侵华战争。4月，英法联军占领舟山。5、6月，英侵略军占大连湾，法侵略军占烟台，封锁渤海湾\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663236813);
