INSERT INTO `fa_admin_log` VALUES (310, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-15 22:21:31\",\"lunar_time\":\"1863-12-02 00:00:00\",\"switch\":\"0\",\"content\":\"包围杭州,高连升军攻杭州凤山门，连破九个堡垒，水师也攻破了江边石垒。初二日，大军逼城，将四门困住。余杭城也被黄少春、杨昌濬等部队将北、东、西北三面包围，留了一个缺口。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663251711);
INSERT INTO `fa_admin_log` VALUES (311, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-16 10:02:23\",\"lunar_time\":\"1864-02-01 00:00:00\",\"switch\":\"0\",\"content\":\"二月，攻克杭州，加太子少保衔，赏穿黄马褂。将士也都分别升官。进城后办的第一件事，就是 设立赈抚局，收养难民，对孤寡妇女儿童都妥善安置。对暴露在街头野外的尸骨，鼓励人民掩埋，给以报酬。又定了几条严禁：严禁军士擅入民居，凡兵勇扰害闾门者斩，拐掠贩卖人口者斩，扰索诈胁绅民者判罪；禁止屠杀耕牛，以保护农业生产。他带兵作战所过之地，虽然饷粮困乏，仍命官兵发散钱米或煮粥分给穷苦人民。浙江富绅杨坊、毛象贤、俞斌等为富不仁。左宗棠上奏弹劾浙江富绅，责令他们筹集巨资，去各地购粮，运回浙江办赈。他还采取以工代赈的办法，修理城墙、衙署等，并教妇女纺织。\\r\\n为了减轻人民负担，他办了几件事： 这几件兴利除弊的事，多是左宗棠以前在湖南办过的，他已积累了经验，驾轻就熟。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663293819);
INSERT INTO `fa_admin_log` VALUES (312, 1, 'admin', '/FYACjLBnds.php/people/add?dialog=1', 'People / 添加', '{\"dialog\":\"1\",\"row\":{\"surnames\":\"曾\",\"name\":\"国荃\",\"zname\":\"沅甫\",\"hname\":\"叔纯（1824年－1890年9月），字沅甫，号叔纯。湖南湘乡白杨坪（今属湖南双峰）人。\",\"sex\":\"男\",\"father_id\":\"\",\"mother_id\":\"\",\"native_city\":\"湖南省\\/湘潭市\\/湘乡市\",\"family_id\":\"2\",\"birthday\":\"1824-01-01 00:00:00\",\"deathday\":\"1890-09-01 00:00:00\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663297034);
