INSERT INTO `fa_admin_log` VALUES (322, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-16 15:21:35\",\"lunar_time\":\"1864-06-01 00:00:00\",\"switch\":\"0\",\"content\":\"与曾国藩“失和”,天京攻陷后，曾国藩向朝廷奏报克复金陵，所有悍贼被一网打尽，并特别指出，城破后，伪幼主积薪于宫中，举火自焚。这就凸现了曾国藩对太平军有攻克全功。不想左宗棠也上一折，称据金陵逃出难民供出伪幼主洪天贵福于同治六月二十一日由东坝逃至广德，被太平军将领黄文金迎入湖州府城，想借伪幼主名号，召集太平军余众。清政府看到左宗棠奏报后，对曾国藩大为不满。曾国藩平生自认为以诚信为本，假如按左宗棠所言，则无异于欺君罔上。于是曾国藩上折反驳左宗棠，称洪天贵福可能已死而黄文金为纠合太平军余众伪称尚存，这是古来常有之事等。言外之意，左宗棠虚张声势，不过是邀功请赏。左宗棠看到此奏后，又上书为自己辩解，对曾国藩言词激烈，口诛笔伐。至此曾左二人的关系已不可挽回，十几年的交情为了各自的名利而付诸东流。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663312968);
