INSERT INTO `fa_admin_log` VALUES (362, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-19 10:26:09\",\"lunar_time\":\"1968-08-05 00:00:00\",\"switch\":\"0\",\"content\":\"八月初五日左宗棠到达天津，初十日到北京。\\r\\n八月十五日，宗棠觐见了慈安、慈禧两宫皇太后。他当了七年的巡抚、总督，和朝廷奏折来往频繁，但见皇太后和皇上还是第一次。太后仍害怕陕甘回军打过来,太后又问：“西北何时可以平定？” 宗棠考应声说：“需要五年。”\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663555192);
