INSERT INTO `fa_admin_log` VALUES (377, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"27\",\"solar_time\":\"2022-09-19 11:41:31\",\"lunar_time\":\"1870-05-01 00:00:00\",\"switch\":\"0\",\"content\":\"天津教案,在天津所发生的一场震惊中外的教案。天津民众为反对天主教会在保教国（法国）武力庇护下的肆行宣教活动，攻击天主教教会机构而造成数十人被谋杀。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663558949);
INSERT INTO `fa_admin_log` VALUES (378, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/216?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"27\",\"solar_time\":\"1870-05-30 00:00:00\",\"lunar_time\":\"1870-05-01 00:00:00\",\"content\":\"天津发生了多起拐骗儿童的事件，还发现幼儿幼女的尸体，城中人心惶惶，六月十八日，天津民团盘查到一名拐犯武兰珍，武犯供认是受法国教堂王三指示，麻药也是王三供给的。由于牵涉外国人，清官府不敢轻率从事，由道员周家勋、知府张光藻、知县刘杰三位地方主管，带领众人押解武兰珍到天主堂，找王三对质。本来群众殴打不过是件小事，可是法国驻津领事丰大业却以官方未认真弹压为借口，小题大做，竟携带两杆洋枪，和一批随从手持利刃，直闯进三口通商大臣崇厚的衙门。找到崇厚后，就开枪射击，幸而崇厚躲得快，逃进了内室，才幸免于难。丰大业这个帝国主义分子咆哮了一阵，将官署内的什物文件捣毁一空，才悻悻离开衙门。半路上正遇到从天主堂转回的刘杰，丰大业又向刘杰开枪，打伤了一名家丁。围观的群众怒不可遏，忍无可忍，哪能容许帝国主义分子在中国街道上任意开枪杀人？群众一哄而上，乱拳之下，将丰大业击毙。天津人民和全中国人民一样，受外国人压迫好几十年，憋了一肚子气，这时突然发泄出来。一些市民立即鸣锣，把全城人民聚集起来，焚毁教堂和一些外国人的房屋，打死十余名法国人、三名俄国人，以及一些中国教民。这样事情就闹大了。事件发生后，京畿和外省都受到震动。首先是法国公使向清政府提出抗议，并以武力威胁，随之英、美、俄、意等国也联合抗议，并出动军舰在沿海巡游示威。\"},\"ids\":\"216\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663559069);
