INSERT INTO `fa_admin_log` VALUES (496, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 21:10:09\",\"lunar_time\":\"1879-02-01 00:00:00\",\"switch\":\"0\",\"content\":\"原任浙江巡抚杨昌濬罢官在家，应左宗棠奏荐，来兰州接替已故刘典帮办陕甘军务的工作。在他的想象中，西北是一片荒凉景象，大漠冷落，飞沙走石，人迹稀少。当他进入陕甘境后，看到大道两旁杨柳成行，高耸云天，浓荫蔽日，枝头鸟鸣蝉噪，春意盎然，路上行人喧阗，宛若塞外江南，大出他的意外。高兴之余，就吟成一首咏左公柳的诗《恭诵左公西行甘棠》：大将筹边尚未还，湖湘子弟满天山；新栽杨柳三千里，引得春风度玉关。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663765833);
