INSERT INTO `fa_admin_log` VALUES (499, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 21:12:10\",\"lunar_time\":\"1880-04-18 00:00:00\",\"switch\":\"0\",\"content\":\"率领亲军 1000 余人离开肃州大本营。前往新疆哈密。在队伍中，有几名壮士抬着一口空棺材，随着他的乘舆后面。这一列长长的队伍，旌旗招展，士饱马腾，在荒凉的戈壁滩上，缓缓地迎着苍凉的落日前进。这一幅悲壮的场景，显示了西征军全体将士不获胜利决不生还的决心，也显示了中国人民不可轻侮、中国领土不容侵占的神圣意志。左宗棠的这一勇敢、爱国的行动和决心，感动了全国人民，也震惊了中外。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663765976);
