INSERT INTO `fa_admin_log` VALUES (519, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 21:25:06\",\"lunar_time\":\"1880-09-01 00:00:00\",\"switch\":\"0\",\"content\":\"禁种罂粟 鸦片销声匿迹\\r\\n不知从哪儿听到的消息，说他的长孙抽鸦片烟，他勃然大怒，立命家人将孙子拿来，予以处死。只有几位姑奶奶是在左宗棠前唯一可以说话的人，她们拉住父亲苦苦相劝，告知他大孙子并没有抽烟的嗜好，不过偶尔抽一两口玩玩的。宗棠才息怒，命将他禁锢在书房内，如果再犯抽烟的毛病，那就非处死不可了。一个月之后，他没有再抽烟，这才放了他出来。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663766728);
