INSERT INTO `fa_admin_log` VALUES (537, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/317?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1882-02-18 00:00:00\",\"lunar_time\":\"1882-01-01 00:00:00\",\"content\":\"他率随从出省城阅兵，二月初一日到扬州，初二日道经高邮，视察南运河工程，初五日到清江浦，一方面调阅江北各营，一方面巡视运河堤工和淮河形势，随后沿中运河，历顺清河、张福口、运口等处，视察礼河正坝\"},\"ids\":\"317\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663768953);
INSERT INTO `fa_admin_log` VALUES (538, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/319?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1882-04-12 00:00:00\",\"lunar_time\":\"1882-02-25 00:00:00\",\"content\":\"到南京。通过亲自勘探研究，他对淮河水害有了一定的了解，定出了兴修水利的方案。\"},\"ids\":\"319\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663768966);
INSERT INTO `fa_admin_log` VALUES (539, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/320?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1882-05-17 00:00:00\",\"lunar_time\":\"1882-04-10 00:00:00\",\"content\":\"左宗棠离开南京，前往镇江、常州、苏州、上海等地。此行目的是：一方面检阅江南营伍，另一方面视察上海制造局的兵器生产，积极备战，以对付列强特别是法国迫在眉睫的侵略。\\r\\n十六日晚他乘坐 “满江红” 船抵达苏州胥门码头，随即上岸到抚台衙门拜会卫静澜巡抚，商讨江苏的治理情况，十七日下了大雨，十八日天晴，到大校场阅兵。二十日离开苏州，“满江红” 船由 “白云” 轮船拖带，经由淀山湖、松江，于二十二日下午 3 时驶入上海黄浦江中，制造局总办刘兴锐派 “江安” 轮船迎接。“满江红” 靠拢上海码头后，地方官员都来迎接拜会，左宗棠只接见了几位重要官员，如上海道、江苏藩司、制造局总办等，其余人均辞谢。码头上备有绿呢大轿，准备迎接他去行馆休憩，也被他辞却，为了避免糜费，他决定留住船上。\"},\"ids\":\"320\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663768997);
INSERT INTO `fa_admin_log` VALUES (540, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:03:25\",\"lunar_time\":\"1882-04-27 00:00:00\",\"switch\":\"0\",\"content\":\"起程回南京，完成了第一次海防巡视。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769019);
INSERT INTO `fa_admin_log` VALUES (541, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/321?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1882-11-11 00:00:00\",\"lunar_time\":\"1882-10-01 00:00:00\",\"content\":\"的身体更差了，记忆力也大不如前，于是上疏请予开缺。 辞职\\r\\n朝廷没有同意他的请求，温旨慰留，给假三个月养病。\"},\"ids\":\"321\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769032);
INSERT INTO `fa_admin_log` VALUES (542, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:03:55\",\"lunar_time\":\"1882-10-24 00:00:00\",\"switch\":\"0\",\"content\":\"左宗棠得了第 12 个孙儿，即孝勋的第二子，宗棠为他取名念恒，字南孙，意思是他在南京总督任内出生的。这是宗棠生前最后得到的一个孙子。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769047);
