INSERT INTO `fa_admin_log` VALUES (544, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/324?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1883-02-08 00:00:00\",\"lunar_time\":\"1883-01-01 00:00:00\",\"content\":\"建林则徐、陶澍二公祠于江宁。建林文忠、陶文毅二公祠于江宁。\"},\"ids\":\"324\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769137);
INSERT INTO `fa_admin_log` VALUES (545, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:05:40\",\"lunar_time\":\"1883-02-01 00:00:00\",\"switch\":\"0\",\"content\":\"视察朱家山河工程，至句容，赤山湖工竣。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769153);
INSERT INTO `fa_admin_log` VALUES (546, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:06:00\",\"lunar_time\":\"1883-03-01 00:00:00\",\"switch\":\"0\",\"content\":\"法攻越南，破南定。公疏请筹办海防，并创立渔团。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769172);
INSERT INTO `fa_admin_log` VALUES (547, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"32\",\"solar_time\":\"2022-09-21 22:06:34\",\"lunar_time\":\"1883-04-01 00:00:00\",\"switch\":\"0\",\"content\":\"诏左宗棠调江南淮楚各军赴前敌抗法，并诏李鸿章赴广东督办越南军务。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769222);
INSERT INTO `fa_admin_log` VALUES (548, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:07:24\",\"lunar_time\":\"1883-06-01 00:00:00\",\"switch\":\"0\",\"content\":\"法攻越南益急。公遣王德榜自永州解济边军，募广勇数千人，并请身赴滇粤督师，清廷止之。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769257);
INSERT INTO `fa_admin_log` VALUES (549, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:07:48\",\"lunar_time\":\"1883-09-01 00:00:00\",\"switch\":\"0\",\"content\":\"出阅渔团，至崇明，还至清江视察运河堤工，议复淮河故道。在途染病。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769282);
INSERT INTO `fa_admin_log` VALUES (550, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:08:11\",\"lunar_time\":\"1883-10-01 00:00:00\",\"switch\":\"0\",\"content\":\"还江宁。自陈衰病，奏请开缺。诏赏假两个月，命公促王德榜成军出关。公檄王德榜于永州募十营，号“恪靖定边军”，筹军火饷需，助其行。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769300);
INSERT INTO `fa_admin_log` VALUES (551, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/324?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1883-02-08 00:00:00\",\"lunar_time\":\"1883-01-01 00:00:00\",\"content\":\"建林则徐、陶澍二公祠于江宁。\"},\"ids\":\"324\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769319);
INSERT INTO `fa_admin_log` VALUES (552, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/324?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1883-02-08 00:00:00\",\"lunar_time\":\"1883-01-01 00:00:00\",\"content\":\"他在南京为陶澍和林则徐建立了陶、林二公祠，合祀二位先行者，他写了一副对联：三吴颂遗爱，鲸浪初平，治水行盐，如公皆不朽；卅载接音尘，鸿泥偶踏，湘间邗上，今我复重来。\"},\"ids\":\"324\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769335);
INSERT INTO `fa_admin_log` VALUES (553, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/324?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1883-02-08 00:00:00\",\"lunar_time\":\"1883-01-01 00:00:00\",\"content\":\"他在南京为陶澍和林则徐建立了陶、林二公祠，合祀二位先行者，他写了一副对联：三吴颂遗爱，鲸浪初平，治水行盐，如公皆不朽；卅载接音尘，鸿泥偶踏，湘间邗上，今我复重来。他离南京沿江而下，经里河、下河，视察海岸各盐场，又巡视范堤和清水潭等堤工程进度。二月十八日到朱家山河工地，王德榜军队正在开凿长十余里高数十丈的石脊，遇到困难。左宗棠教他们凿石穿孔，装入火药爆破，解决了施工问题。二十七日又到句容勘验新筑成的圩堤，教筑堤人员沿堤埂种植桑秧防护。二十九日，由二三汊河顺流视察通济门石闸。\"},\"ids\":\"324\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769346);
INSERT INTO `fa_admin_log` VALUES (554, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:09:22\",\"lunar_time\":\"1882-11-01 00:00:00\",\"switch\":\"0\",\"content\":\"修筑范堤。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769375);
INSERT INTO `fa_admin_log` VALUES (555, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:09:42\",\"lunar_time\":\"1883-05-01 00:00:00\",\"switch\":\"0\",\"content\":\"范堤筑成。秋天发大水，水涨到一丈六尺余。幸而几项水利工程已完成，就依次开放车逻、南关各堤，里下河农田得到保障，是年仍获得丰收。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769405);
INSERT INTO `fa_admin_log` VALUES (556, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/329?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1883-10-01 00:00:00\",\"lunar_time\":\"1883-09-01 00:00:00\",\"content\":\"出阅渔团，至崇明，还至清江视察运河堤工，议复淮河故道。在途染病。他到江阴、靖江、通州（南通）、海门、崇明、吴淞等地，检阅渔团水勇，水勇们演习泅水、凫水、爬桅、超跃、打靶等，技艺娴熟，“精能过人”，参观者都诧为罕见\"},\"ids\":\"329\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769436);
INSERT INTO `fa_admin_log` VALUES (557, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:10:45\",\"lunar_time\":\"1883-11-05 00:00:00\",\"switch\":\"0\",\"content\":\"左宗棠正在南京总督署内养病，国内金融界发生了一件虽不算大、影响却不小的事件：胡雪岩开设的阜康银号突然倒闭。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769462);
INSERT INTO `fa_admin_log` VALUES (558, 1, 'admin', '/FYACjLBnds.php/people/add?dialog=1', 'People / 添加', '{\"dialog\":\"1\",\"row\":{\"surnames\":\"王\",\"name\":\"德榜（1837-1893），字朗清，广东东莞石排埔心村人，父经商湖南，生于湖南江华县\",\"zname\":\"朗清\",\"hname\":\"\",\"sex\":\"男\",\"father_id\":\"\",\"mother_id\":\"\",\"native_city\":\"广东省\\/东莞市\\/石排镇\",\"family_id\":\"8\",\"birthday\":\"1837-01-01 00:00:00\",\"deathday\":\"1893-01-01 00:00:00\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769589);
