INSERT INTO `fa_admin_log` VALUES (561, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/334?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"48\",\"solar_time\":\"2022-10-16 00:00:00\",\"lunar_time\":\"1884-01-01 00:00:00\",\"content\":\"王德榜军抵南宁。\"},\"ids\":\"334\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769654);
INSERT INTO `fa_admin_log` VALUES (562, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:14:41\",\"lunar_time\":\"1884-01-05 00:00:00\",\"switch\":\"0\",\"content\":\"这时左宗棠的目疾更厉害了，再次奏请开缺，并推荐由曾国荃、杨昌濬、裕禄三人之一取代他。在谕旨尚未批下时，正月初五日正当大家高高兴兴过年的时候，他却抱病又到了清江，研究引淮入故道的工程，二十三日回到扬州，又去崇明、上海等地检阅渔团和海防工事。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769693);
INSERT INTO `fa_admin_log` VALUES (563, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:14:59\",\"lunar_time\":\"1884-02-01 00:00:00\",\"switch\":\"0\",\"content\":\"朱家山河工程落成，二月初二他到朱家山河视察，初三日回到南京，这是他任两江总督的最后一次出巡。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769710);
