INSERT INTO `fa_admin_log` VALUES (568, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"34\",\"solar_time\":\"2022-09-21 22:17:47\",\"lunar_time\":\"1884-04-01 00:00:00\",\"switch\":\"0\",\"content\":\"李鸿章与法使福禄诺在天津签订了《中法简明条约》，约中规定：中国承认法国占领越南，撤回驻在越南的军队，并允许法国商品由云南、广西自由输入内地等。这项条约严重地损害了中越两国人民的利益，又向侵略者打开了中国西南大门。一时弹劾李鸿章的奏章多达 47 封。社会上一些人称他为 “汉奸”。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769917);
INSERT INTO `fa_admin_log` VALUES (569, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"48\",\"solar_time\":\"1884-06-01 00:00:00\",\"lunar_time\":\"\",\"switch\":\"0\",\"content\":\"观音桥之战，法军阵亡 24 人，清军伤亡 300 余人。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663769972);
INSERT INTO `fa_admin_log` VALUES (570, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:19:45\",\"lunar_time\":\"2022-09-21 22:19:45\",\"switch\":\"0\",\"content\":\"左宗棠抵达京师,朝廷着他管理神机营，这是卫戍京师的重要任务。主战派的首领是醇亲王奕 ，其他朝内外大臣还有翁同龢、陈宝琛、张之洞、彭玉麟、刘锦棠等。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770003);
INSERT INTO `fa_admin_log` VALUES (571, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/343?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-10-16 00:00:00\",\"lunar_time\":\"1884-05-20 00:00:00\",\"content\":\"左宗棠抵达京师,朝廷着他管理神机营，这是卫戍京师的重要任务。主战派的首领是醇亲王奕 ，其他朝内外大臣还有翁同龢、陈宝琛、张之洞、彭玉麟、刘锦棠等。\"},\"ids\":\"343\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770017);
INSERT INTO `fa_admin_log` VALUES (572, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:20:29\",\"lunar_time\":\"1884-05-30 00:00:00\",\"switch\":\"1\",\"content\":\"一支法国舰队驶入福建马尾军港。马尾是中国内港，船政大臣何如璋竟不敢阻止，他是李鸿章的亲信，深知李鸿章一意主和，为了避免与法军冲突，不惜放弃国家主权，竟让这支法国舰队停泊在中国的军事要港达一个多月。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770053);
INSERT INTO `fa_admin_log` VALUES (573, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:21:56\",\"lunar_time\":\"1884-06-01 00:00:00\",\"switch\":\"0\",\"content\":\"孤拔率领法舰 13 艘也驶抵台湾海峡。六月十五日，突然向基隆发动进攻，旋即登陆占领基隆炮台。督办台湾防务的福建巡抚刘铭传率部队进行了抵抗，将法军击退。法军在基隆吃了败仗后，法国政府十分恼怒，一方面通知停泊在马尾港的法舰队准备策应，一方面命令驻华代理公使谢满禄向清政府提出最后通牒，并于七月一日下旗离开京师。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770152);
INSERT INTO `fa_admin_log` VALUES (574, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:23:16\",\"lunar_time\":\"1884-07-03 00:00:00\",\"switch\":\"0\",\"content\":\"马江之役，福建水师全军覆灭\\r\\n，法国驻福州领事通告何如璋：“本日对华开战！” 何如璋听到这样重大的消息，却坚守秘密，不做任何准备，还对部将们说：“昨天还得到李相电告，和议大有进步。你们听到的开战消息必系谣传。” 然而马尾港内的法国舰队却毫不客气，立即大炮齐发，只用一个多小时就击沉了港内全部中国船只，包括由 11 艘兵船组成的福建水师和另外 19 艘商船。这支由左宗棠苦心经营的马尾船政局制造的兵船组成的舰队，在毫无准备的情况下仓促应战，因为战机全失，虽然进行了英勇抵抗，船只相继被击沉，福建水师将士阵亡 760 余人。法舰又开炮击毁马尾造船厂，然后退出马尾，遭海岸炮台还击，被击伤数艘。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770209);
INSERT INTO `fa_admin_log` VALUES (575, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"32\",\"solar_time\":\"2022-09-21 22:23:36\",\"lunar_time\":\"1884-07-06 00:00:00\",\"switch\":\"0\",\"content\":\"即马江之败三天后，宣布对法开战。朝中的主战派以奕、左宗棠为首，态度很坚决。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770240);
INSERT INTO `fa_admin_log` VALUES (576, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"2022-09-21 22:24:11\",\"lunar_time\":\"1884-07-15 00:00:00\",\"switch\":\"0\",\"content\":\"傍晚，左宗棠来到醇亲王官邸，要求奕 [插图] 同意他亲赴福建前线督师，与法军决一雌雄。奕 [插图] 见他要求很坚决，马上就要去前线，急如星火，就劝他不要着急，少安毋躁。其实，奕 [插图] 同情他，也赞成他去，认为唯有他去可以一拼。奕 [插图] 立即上奏太后。三天后，诏令左宗棠以钦差大臣督办福建军务，命他急速赴福建督师。奕 [插图] 当即写信给总理衙门，叙述当时左宗棠谈话时的情况说：“左相犹如伏波将军（马援）的气概：‘老当益壮，男儿要当死于边野，以马革裹尸还’耳。”\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770269);
