INSERT INTO `fa_admin_log` VALUES (587, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/353?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1884-11-18 00:00:00\",\"lunar_time\":\"1884-10-27 00:00:00\",\"content\":\"左宗棠抵达福州，全城官员聚集在洪山桥接官亭迎接，士绅和老百姓则在浙绍会馆迎候。\\r\\n在《中法马江战役之回忆》一书中记载说：“钦差大臣左宗棠进入福州时，威风凛凛，旗帜飘扬，上面大书‘恪靖侯左’字样。队伍两行，个个肩荷洋枪，步伐整齐，后面一人乘肥马，执长鞭，头戴双眼花翎，身穿黄绫马褂，主将左宫保是也。” 自马江战败后，福州官民一夕数惊，风声鹤唳，“一见宫保，无异天神降临，所以敬礼如此。”《申报》还说：“想帷幄运筹，谟独裕，蠢尔法人，当不难灭此朝食也。” 福建人十分信任左宗棠，自他到达后，福州人心大定。\"},\"ids\":\"353\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770875);
