INSERT INTO `fa_admin_log` VALUES (590, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/354?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"1\",\"solar_time\":\"1885-01-16 00:00:00\",\"lunar_time\":\"1884-12-26 00:00:00\",\"content\":\"正当除夕临近之际，法国军舰突然聚集在马祖澳，准备趁中国官员欢度新年之际，发动一场突袭。左宗棠和杨昌濬得到消息后，立即同往海防前沿巡视。\\r\\n二十八日，有几艘法舰由白犬洋开来试探，左宗棠正在长门要塞，即下令开炮轰击，法舰见防备森严，只得灰溜溜地掉头开走了。\"},\"ids\":\"354\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663770953);
