INSERT INTO `fa_admin_log` VALUES (601, 1, 'admin', '/FYACjLBnds.php/event/edit/ids/359?dialog=1', 'Event / 编辑', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"48\",\"solar_time\":\"1885-03-17 00:00:00\",\"lunar_time\":\"1885-02-01 00:00:00\",\"content\":\"王德榜率定边军，会广西提督冯子材，大破法兵于镇南关、谅山。\\r\\n镇南关 — 谅山大捷,越南人民得知清军大胜，纷纷闻风响应起义。越南官员将北宁等处义民集中起来，成立 “忠义五大团”，打出冯子材军旗号，自愿为清军挑饭、做向导，随军助剿。中国国内人民也都欢欣鼓舞。\\r\\n当法军战败的消息传到巴黎时，引起了法国全国上下的震动。发动侵略战争的茹费里内阁在法国人民的抗议和反对派的攻击下随之倒台。\\r\\n法人复请议和，清廷下诏停战。\"},\"ids\":\"359\"}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663771325);
