INSERT INTO `fa_admin_log` VALUES (617, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"50\",\"solar_time\":\"2022-09-21 22:52:48\",\"lunar_time\":\"1907-06-01 00:00:00\",\"switch\":\"0\",\"content\":\"福州船政局于光绪三十三年（1907 年）因 “管理不善，经费支绌” 而停办，辛亥革命后，孙中山的国民政府恢复了船政局，改名 “马尾造船所”，但规模不大，建造的船也不多。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663771986);
