INSERT INTO `fa_admin_log` VALUES (625, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"28\",\"solar_time\":\"1843-06-01 00:00:00\",\"lunar_time\":\"\",\"switch\":\"0\",\"content\":\"在广东花县莲花塘吸收了第一个拜上帝会员李敬芳，随后少年时代的同学冯云山与族弟洪仁玕参加。并作诗稱“天父天兄太平時，保汝處處有飯喫。耕田首先在根苗，殺盡韃子閻羅妖。”又作“手握乾坤杀伐权，斩邪留正解民悬。手持三尺定山河，太平一统乐如何。”\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663902350);
INSERT INTO `fa_admin_log` VALUES (626, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"28\",\"solar_time\":\"1844-06-01 00:00:00\",\"lunar_time\":\"\",\"switch\":\"0\",\"content\":\"与表亲冯云山、族弟洪仁玕从梁发的《劝世良言》宣传书籍中吸取某些基督教教义，后来自行洗礼。是年，洪秀全和冯云山到广西贵县一带传教，洪不久便返回广东，冯留在广西发展，在当地的信徒日增。\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663902813);
