INSERT INTO `fa_admin_log` VALUES (629, 1, 'admin', '/FYACjLBnds.php/event/add?dialog=1', 'Event / 添加', '{\"dialog\":\"1\",\"row\":{\"people_id\":\"28\",\"solar_time\":\"2022-09-23 11:18:26\",\"lunar_time\":\"\",\"switch\":\"0\",\"content\":\"第三次跑到广州参加科举考试，不出意外，再次落榜。这下精神上彻底垮了，回到家之后，自暴自弃，染病卧床，期间开始阅读《劝世良言》，认识了什么是基督，什么是上帝，如获至宝\"}}', '127.0.0.1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/105.0.0.0 Safari/537.36', 1663903169);
