INSERT INTO `fa_event` VALUES (17, 1, '1815-02-09 00:00:00', '1815-01-01 00:00:00', '随祖父松野公读书识字。家中有两间屋子，屋前有几株高高的梧桐树，还有一口池塘，取名为 “梧塘书屋”。', 1663072328, 1663072328);
INSERT INTO `fa_event` VALUES (15, 18, '1812-07-14 00:00:00', '1812-06-06 00:00:00', '胡林翼生于嘉庆十七年六月初六日酉时', 1663065113, 1663065113);
INSERT INTO `fa_event` VALUES (16, 1, '1812-11-10 00:00:00', '1812-10-07 00:00:00', '左宗棠出生湖南省湘阴县东乡左家塅', 1663065730, 1663065730);
INSERT INTO `fa_event` VALUES (18, 1, '1816-01-29 00:00:00', '1816-01-01 00:00:00', '父亲在长沙左氏宗祠内设馆授徒，并将全家从湘阴乡间迁来，兄弟三人都跟着父亲读书', 1663072544, 1663072544);
INSERT INTO `fa_event` VALUES (19, 8, '1816-01-29 00:00:00', '1816-01-01 00:00:00', '考中秀才', 1663072625, 1663072625);
INSERT INTO `fa_event` VALUES (20, 1, '1817-02-16 00:00:00', '1817-01-01 00:00:00', '始读《论语》《孟子》', 1663072670, 1663072670);
INSERT INTO `fa_event` VALUES (21, 7, '1819-01-26 00:00:00', '1819-01-01 00:00:00', '考上秀才', 1663072719, 1663072719);
INSERT INTO `fa_event` VALUES (22, 1, '1820-02-14 00:00:00', '1820-01-01 00:00:00', '初学学八股文', 1663072758, 1663072758);
INSERT INTO `fa_event` VALUES (23, 1, '1821-02-03 00:00:00', '1821-01-01 00:00:00', '长沙城谣言，纸人剪辫', 1663072803, 1663072803);
INSERT INTO `fa_event` VALUES (24, 1, '1822-01-23 00:00:00', '1822-01-01 00:00:00', '做了个梦去了一座古寺', 1663072826, 1663072826);
INSERT INTO `fa_event` VALUES (25, 1, '1823-02-11 00:00:00', '1823-01-01 00:00:00', '开始留意书法', 1663072921, 1663072921);
INSERT INTO `fa_event` VALUES (26, 8, '1825-02-18 00:00:00', '1825-01-01 00:00:00', '充湘阴县拔贡生', 1663072971, 1663072971);
INSERT INTO `fa_event` VALUES (27, 1, '1826-02-07 00:00:00', '1826-01-01 00:00:00', '参加童子试', 1663073133, 1663073133);
INSERT INTO `fa_event` VALUES (28, 1, '1827-05-26 00:00:00', '1827-05-01 00:00:00', '应府试，取第二名；因母病，没能参加院试，因而这次没有取得秀才资格。', 1663073215, 1663073215);
INSERT INTO `fa_event` VALUES (29, 1, '1827-12-04 00:00:00', '1827-10-16 00:00:00', '医生说母亲身子太虚，要用上好人参滋补，父亲各处借贷，好不容易买了不到一两的参，但是于事无补\r\n父亲为筹办母亲的殡葬费和生前的医药费，欠了几百两银子的债，只好将家中什物拿去典当，掌柜很瞧不起这个穷少年，没有少给他白眼。\r\n\r\n', 1663073298, 1663073298);
INSERT INTO `fa_event` VALUES (30, 1, '1829-02-04 00:00:00', '1829-01-01 00:00:00', '居丧期间，从书肆买到两本书：一本是顾炎武的《郡国利病书》，另一本是顾祖禹的《读史方舆纪要》，如获至宝，早晚研读，他还购到了齐召南的《水道提纲》等书，他还藏有一部《皇朝经世文篇》，这部书名义上是贺长龄主编。', 1663073372, 1663073372);
INSERT INTO `fa_event` VALUES (31, 1, '1830-02-11 00:00:00', '1830-01-18 00:00:00', '父亲病逝，父母合葬在长沙城北史家坡，这时姐姐们都已出嫁，一个美满的十口之家，就只剩下兄弟二人，还有一位寡嫂，领养了一个孤侄。', 1663073409, 1663073409);
INSERT INTO `fa_event` VALUES (32, 19, '1830-01-25 00:00:00', '1830-01-01 00:00:00', '母亲去世回籍居丧，名为 “丁忧”', 1663074088, 1663074110);
INSERT INTO `fa_event` VALUES (33, 1, '1830-01-26 00:00:00', '1830-01-02 00:00:00', '对贺长龄仰慕已久，因此去向他求教。贺长龄也很喜欢这个年轻人。长龄藏书很多，无论什么书，他都愿借给宗棠。贺长龄对左宗棠说：“方今天下真正的人才十分缺乏，以后千万不要急急忙忙找一个小官职就当，这样会屈限你的成就。要耐心些等待，干那些能发挥你的才能的事业！”', 1663074269, 1663074269);
INSERT INTO `fa_event` VALUES (34, 19, '1831-02-13 00:00:00', '1831-01-01 00:00:00', '居丧一年后，回江苏布政史原任去了', 1663074322, 1663074322);
INSERT INTO `fa_event` VALUES (35, 20, '1830-01-25 00:00:00', '1830-01-01 00:00:00', '因母亲去世回籍居丧，名为 “丁忧”', 1663074376, 1663074376);
INSERT INTO `fa_event` VALUES (36, 20, '1831-02-13 00:00:00', '1831-01-01 00:00:00', '原任湖北学政，也因丁忧在家，长沙城南书院就请他主持讲席。熙龄不重视八股文，和哥哥长龄#19#一样，注重 “经世致用” 的实学。他用汉宋以来实学家的著作教学生', 1663074435, 1663075539);
INSERT INTO `fa_event` VALUES (37, 1, '1831-02-13 00:00:00', '1831-01-01 00:00:00', '宗棠由长龄#19#介绍进城南书院。他一是慕熙龄的名去读书，另外也是为了得到一点膏火费，以维持生活。', 1663074474, 1663075523);
INSERT INTO `fa_event` VALUES (38, 1, '1831-02-13 00:00:00', '1831-01-01 00:00:00', '湖南巡抚吴荣光在长沙办了一个 “湘水校经堂”，一连得了七次第一，引起了吴荣光的注意。', 1663074525, 1663074525);
INSERT INTO `fa_event` VALUES (39, 1, '1832-02-02 00:00:00', '1832-01-01 00:00:00', '由于急切希望考中举人，于是借钱捐了一个与秀才相当的监生，这样就可以直接应乡试，乡试发榜，宗植中了解元（第一名举人），宗棠落选了。\r\n道光皇帝五十大寿，下诏再择优录取一些，选中了六份，左宗棠的卷子居首位', 1663074863, 1663074863);
INSERT INTO `fa_event` VALUES (40, 1, '1832-08-26 00:00:00', '1832-08-01 00:00:00', '左宗植#8#和左宗棠一同参加乡试，试后尚未发榜，宗植就带着弟弟来到湘潭，为他完婚。周家是有钱人家，宗棠是穷书生，他就招赘在周家。', 1663074948, 1663075493);
INSERT INTO `fa_event` VALUES (41, 3, '1833-01-21 00:00:00', '1832-12-01 00:00:00', '有谣言宗棠进京路上大病，担心成疾，肝气上犯', 1663075022, 1663075022);
INSERT INTO `fa_event` VALUES (42, 1, '1833-02-20 00:00:00', '1833-01-01 00:00:00', '和宗植齐#8#赴京师会试，这是他们第一次去京师，不幸二人均落第。在京师时宗棠写了有名的《燕台杂感》七律八章', 1663075117, 1663075479);
INSERT INTO `fa_event` VALUES (43, 1, '1833-02-21 00:00:00', '1833-01-02 00:00:00', '他在京师还拜会了一位大名人、他的老师的老师阮元', 1663075467, 1663075575);
INSERT INTO `fa_event` VALUES (44, 1, '1833-02-21 00:00:00', '1833-01-02 00:00:00', '胡林翼#18#住在父亲家中，左宗棠来胡家拜访，二人一见就成为好友。二人谈论古今大政，臧否时事。对朝廷腐败、官吏无能、人民生活困苦、各地闹事，西方列强日益逼近中国、虎视眈眈，他们深感忧虑，都预感到天下将大乱。', 1663075620, 1663075629);
INSERT INTO `fa_event` VALUES (45, 1, '1833-07-17 00:00:00', '1833-06-01 00:00:00', '帮欠300两债的老太婆还债', 1663075739, 1663075739);
INSERT INTO `fa_event` VALUES (46, 1, '1833-10-13 00:00:00', '1833-09-01 00:00:00', '向周家租了几间房子，在周家桂在堂西院，种植桑树，养蚕、缫丝', 1663075772, 1663075772);
INSERT INTO `fa_event` VALUES (47, 1, '1836-01-18 00:00:00', '1835-12-01 00:00:00', '第二次赴京会试。', 1663076241, 1663076241);
INSERT INTO `fa_event` VALUES (48, 1, '1836-02-17 00:00:00', '1836-01-01 00:00:00', '会试取中揭晓，宗棠在最后一名，被刷下来', 1663076390, 1663076390);
INSERT INTO `fa_event` VALUES (49, 18, '1836-02-17 00:00:00', '1836-01-01 00:00:00', '考上进士', 1663076409, 1663076409);
INSERT INTO `fa_event` VALUES (50, 1, '1836-07-14 00:00:00', '1836-06-01 00:00:00', '他住在周家西楼，专心研究地理，画出明、元、宋朝直至古代的地图。周夫人协助他，经过年余，图画成了', 1663076487, 1663076487);
INSERT INTO `fa_event` VALUES (51, 1, '1836-07-14 00:00:00', '1836-06-01 00:00:00', '一贫如洗，会试又连连失意，但他仍满怀壮志，写了一副对联挂在书房内：\r\n身无半亩，\r\n心忧天下；\r\n读破万卷，\r\n神交古人。', 1663076510, 1663076510);
INSERT INTO `fa_event` VALUES (52, 1, '1836-10-10 00:00:00', '1836-09-01 00:00:00', '纳副室张夫人为妾', 1663076559, 1663076559);
INSERT INTO `fa_event` VALUES (53, 1, '1837-02-05 00:00:00', '1837-01-01 00:00:00', '应吴荣光召，主讲醴陵渌江书院，任山长（校长）。书院很穷，经常请不到教师。宗棠不在乎生活清苦，对教学抓得很紧，管理严格，奖勤罚懒，对不用功和说假话的学生，就停发膏火费，用它奖给潜心苦读的人。学生进步很快。', 1663077080, 1663077157);
INSERT INTO `fa_event` VALUES (54, 1, '1837-04-05 00:00:00', '1837-03-01 00:00:00', '知县叫宗棠为陶澍写对联\r\n春殿语从容，廿载家山，印心室在；\r\n大江流日夜，八州子弟，翘首公归。', 1663077550, 1663077550);
INSERT INTO `fa_event` VALUES (55, 21, '1837-04-06 00:00:00', '1837-03-02 00:00:00', '两江总督陶澍到江西阅兵，顺便请假回原籍扫墓，道经醴陵，住在行馆中，看到宗棠的对联，约见订交而别。他得知宗棠次年将赴京会试，临别时再三嘱咐：“会试毕，不论中与不中，务必绕道南京，来督署衙门盘桓几天。', 1663080284, 1663080284);
INSERT INTO `fa_event` VALUES (56, 1, '1837-12-27 00:00:00', '1837-12-01 00:00:00', '两江总督', 1663080387, 1663080387);
INSERT INTO `fa_event` VALUES (57, 1, '1838-01-26 00:00:00', '1838-01-01 00:00:00', '第三次（最后一次）会试，曾住在周振之家，会试落第，绕道南京，拜会陶澍，左宗棠下定决心，以后不再参加科举考试了。他又注意起农业，开了试验田，写了农书，还准备做一辈子的农人。', 1663080609, 1663080609);
INSERT INTO `fa_event` VALUES (58, 1, '1838-03-26 00:00:00', '1838-03-01 00:00:00', '写过一篇《广区田图说》，极力推广古代分区种田的区田制，他认为有六善三便，可以节水、省肥、积水保墒、防虫害、省劳力、亩产高等', 1663080627, 1663080627);
INSERT INTO `fa_event` VALUES (59, 1, '1839-07-11 00:00:00', '1839-06-01 00:00:00', '夏天左宗棠来到长沙，又和左宗植相会。识邓显鹤、邹汉勋。钻研地理及历代兵事。于湘潭所居种桑千本，令家人饲蚕、治丝。', 1663080764, 1663080764);
INSERT INTO `fa_event` VALUES (60, 1, '1839-10-07 00:00:00', '1839-09-01 00:00:00', '贺熙龄准备回北京。他的朋友和学生共 10 人会集在长沙城南，饮酒话别，在座的除左宗棠、邓显鹤、罗研生、邹汉勋等人外，左宗植也来了。', 1663080848, 1663080848);
INSERT INTO `fa_event` VALUES (61, 20, '1839-10-07 00:00:00', '1839-09-01 00:00:00', '回北京，特别思念自己最得意的学生左宗棠，就写一首\r\n《舟中怀左季高诗》：\r\n六朝花月毫端扫，\r\n万里江山眼底横。\r\n开口能谈天下事，\r\n读书深抱古人情。', 1663080899, 1663080899);
INSERT INTO `fa_event` VALUES (62, 21, '1839-10-07 00:00:00', '1839-09-01 00:00:00', '陶澍死于南京任所，贺熙龄写信给左宗棠，要他去安化陶家教孤子陶桄读书。宗棠于是遵命到安化小淹陶家坐馆，陶家藏书很多，对他后来的事业有很大的帮助。', 1663081012, 1663081012);
INSERT INTO `fa_event` VALUES (63, 22, '1839-06-03 00:00:00', '1839-04-22 00:00:00', '虎门硝烟', 1663081174, 1663081174);
INSERT INTO `fa_event` VALUES (64, 1, '1840-06-01 00:00:00', '1840-05-02 00:00:00', '在安化小淹陶家坐馆，遍览陶家藏书。第一次鸦片战争爆发，英舰犯广州、浙江，陷定海。林则徐被革职遣戍。公闻警忧愤，多次写信给熙龄#20#先生，论战守机宜。写成《料敌》《定策》等抗英策论6篇。', 1663120476, 1663120476);
INSERT INTO `fa_event` VALUES (65, 1, '1841-01-23 00:00:00', '1841-01-01 00:00:00', '英军占据香港，进犯浙江,他还抱希望于林则徐，认为只有林则徐复出，收拾局面，还能固守东南半壁。但他也慨叹：“恐怕逋翁是出不来了。他只有慨叹 “国家前途茫茫”，也渐渐由积极变为消极', 1663120632, 1663120632);
INSERT INTO `fa_event` VALUES (66, 1, '1842-08-29 00:00:00', '1842-07-24 00:00:00', '中英签订《南京条约》，公愤极，欲买山隐居。胡林翼#18#时至小淹，与公连床谈古今大政，恒至达旦。', 1663120743, 1663120743);
INSERT INTO `fa_event` VALUES (68, 23, '1842-02-10 00:00:00', '1842-01-01 00:00:00', '<海国图志> 成书，提出师夷长技以制夷', 1663121145, 1663121145);
INSERT INTO `fa_event` VALUES (69, 1, '1843-01-30 00:00:00', '1843-01-01 00:00:00', '陶家坐馆，每年得到束脩 200 两银子，省吃俭用，手中有了些积蓄，买田湘阴东乡柳庄，此为有家之始。', 1663140075, 1663140075);
INSERT INTO `fa_event` VALUES (70, 3, '1844-10-12 00:00:00', '1844-09-01 00:00:00', '周夫人率领三个女儿从湘潭迁来柳庄', 1663140170, 1663140170);
INSERT INTO `fa_event` VALUES (71, 1, '1844-02-18 00:00:00', '1844-01-01 00:00:00', '左宗棠那时大部分时间还在安化，每次回到柳庄，他就专心钻研农事。除了水稻外，还种茶、桑、竹和其他树木，也养蚕、种菜、种花等。他雇用了少数有农业经验的长工师傅，农忙时还雇用短工帮忙。成功后，推广到农民中去，让广大农民都获得利益。', 1663140239, 1663140239);
INSERT INTO `fa_event` VALUES (72, 1, '1844-02-18 00:00:00', '1844-01-01 00:00:00', '左宗棠曾去长沙，见到老师贺熙龄#20#。当他回到小淹时，才知道陶澍#21#夫人已病逝。翌年胡林翼#18#来吊岳母之丧，在小淹住了 10 天', 1663140320, 1663140320);
INSERT INTO `fa_event` VALUES (73, 1, '1845-02-07 00:00:00', '1845-01-01 00:00:00', '写了一部《朴存阁农书》，“朴存” 是他自己起的别号，书共十余卷，分门别类叙述各种农事', 1663140366, 1663140366);
INSERT INTO `fa_event` VALUES (74, 1, '1846-10-20 00:00:00', '1846-09-01 00:00:00', '老师熙龄#20#去世，那年八月宗棠的长子孝威出生。熙龄知道了，非常高兴，说：“这孩子该做我的女婿。”\r\n贺熙龄死后，左宗棠写了一副挽联：\r\n宋儒学，汉人文，落落几知心，公自有书遗后世；\r\n定王台，贾傅井，行行重回首，我从何处哭先生！', 1663140449, 1663140449);
INSERT INTO `fa_event` VALUES (75, 9, '1847-05-01 00:00:00', '1847-04-01 00:00:00', '孝宽出生，周夫人#3#体弱，没有奶水，两个男孩都由张夫人哺乳。每次她都先将孝威#10#喂饱了，然后再喂孝宽#11#', 1663140603, 1663141361);
INSERT INTO `fa_event` VALUES (76, 14, '1847-09-09 00:00:00', '1847-08-01 00:00:00', '长女15岁，陶澍#21#独子陶桄#24#也已 16 岁，奉陶澍的遗命，两家结成秦晋之好', 1663140720, 1663140789);
INSERT INTO `fa_event` VALUES (77, 1, '1848-02-05 00:00:00', '1848-01-01 00:00:00', '是年大水，柳庄田禾被淹，家人皆病。公出与郡人士劝富室捐赈救灾，又于族里积谷备荒。', 1663141336, 1663141336);
INSERT INTO `fa_event` VALUES (78, 22, '1848-02-05 00:00:00', '1848-01-01 00:00:00', '任云贵总督', 1663141450, 1663141467);
INSERT INTO `fa_event` VALUES (79, 1, '1848-02-06 00:00:00', '1848-01-02 00:00:00', '林则徐忙要胡林翼#18#去请左宗棠来云贵督署工作，正逢左宗棠大嫂要为儿子世延完婚，另外左宗棠已接受陶家教课的聘约，因此回信婉辞，十分遗憾。', 1663141536, 1663141536);
INSERT INTO `fa_event` VALUES (80, 18, '1848-02-05 00:00:00', '1848-01-01 00:00:00', '从贵州派人来，送了一笔钱给左宗棠，全家这才高高兴兴过了一个丰富的年。', 1663141585, 1663141585);
INSERT INTO `fa_event` VALUES (81, 18, '1848-04-04 00:00:00', '1848-03-01 00:00:00', '胡林翼因丁父忧，回到益阳老家。他经常来小淹陶家，帮助岳母处理家事。旧友重逢，二人都高兴异常，似乎有谈不完的话。', 1663141614, 1663141614);
INSERT INTO `fa_event` VALUES (82, 1, '1849-02-01 00:00:00', '1849-01-01 00:00:00', '陶家搬到长沙，宗棠也在长沙徽国朱文公祠设馆授徒，陶桄#24#仍跟他学习', 1663141711, 1663141774);
INSERT INTO `fa_event` VALUES (83, 1, '1849-03-24 00:00:00', '1849-03-01 00:00:00', '是年复大水，灾民络绎过柳庄。公罄所藏，与周夫人散米俵食，并配丸药与病者，全活甚众。', 1663141752, 1663141752);
INSERT INTO `fa_event` VALUES (84, 1, '1850-01-03 00:00:00', '1849-11-21 00:00:00', '收到林则徐#22#书信，原来是林则徐卸任云贵总督，告病回里，途经长沙，舟靠湘江岸边，特来信约左宗棠速去长沙一会，林则徐是左宗棠素所钦仰的伟人，能得到他的邀请，和他会面，确是没有想到的事，哪能不兴奋！左宗棠匆匆忙忙上船，走过跳板时，大概是因为心情紧张，一脚踏空，落入水中，从人赶忙扶起。进舟舱盥洗更衣后，两人就畅谈起来。天色已晚，林则徐命将官船乘着湘江乱流，驶到西岸岳麓山下一个僻静处停泊。舟中掌起灯，摆上酒，二人一面喝酒，一面纵谈天下古今大事，一直谈了一夜，林则徐的两个儿子也在一旁侍听。一直谈到东方发白，曙鼓频敲，才互相告别\r\n他们谈的内容很广泛，谈到了\r\n天下大势：认为英、法尚不足畏，他的失败是由于朝廷的投降政策。俄罗斯将是今后中国的大敌！\r\n新疆建立行省：林则徐在遣戍新疆时，发现新疆是一片大好河山，但是建制不对\r\n臧否当今人物：林则徐告诉左宗棠，张亮基、胡林翼、黄惺斋是他任云贵总督时的左右手。他们对已故的陶澍和贺长龄都表示钦佩。\r\n一致同意魏源的主张：“师夷长技以制夷。”\r\n林则徐将自己在新疆积累的全部资料送给左宗棠，包括地理、边防以及俄国的动态等。还对左宗棠说：“我已老了，眼看着俄国虎视眈眈，终久会为西北边患。我虽有御俄的志愿，但是力不从心了。多年来留心人才，今后西定新疆，抗击俄人侵略，唯有寄希望于你了。”\r\n临别前他写了一副对联赠给左宗棠：此地有崇山峻岭，茂林修竹；是能读三坟五典，八索九丘。', 1663141852, 1663141852);
INSERT INTO `fa_event` VALUES (85, 22, '1850-11-22 00:00:00', '1850-10-19 00:00:00', '广西天地会起义。清朝廷于是又起用林则徐为钦差大臣，前往广西镇压。当他刚到达广东潮州时，突然染病去世。他在临终前还不忘记左宗棠，命次子聪彝向咸丰皇帝代写遗折，折中一再推荐左宗棠为难得人才', 1663142377, 1663142377);
INSERT INTO `fa_event` VALUES (86, 1, '1850-11-24 00:00:00', '1850-10-21 00:00:00', '左宗棠在长沙黄冕寓馆中，午夜听到林则徐去世噩耗，他和黄冕不禁相对失声痛哭。他写了一封充满激情的悼唁信给大公子镜枫，还写了一副传诵一时的挽联：\r\n附公者不皆君子，间公者必是小人，忧国如家，二百余年遗直在；\r\n庙堂倚之为长城，草野望之若时雨，出师未捷，八千里路大星颓。', 1663142486, 1663142486);
INSERT INTO `fa_event` VALUES (89, 1, '1851-02-01 00:00:00', '1851-01-01 00:00:00', '道光皇帝驾崩，皇帝新即位，特颁恩诏开孝廉方正科，以搜集乡野遗才。郭嵩焘和湘阴县士绅都推荐左宗棠应举。左宗棠早已打算做一辈子 “农夫”，现又准备隐居深山逃避战乱，并不愿进入仕途，因此谢绝了乡里友人的推荐。他既不愿站在清王朝一方，又不愿站在太平天国一方，而是采取中立的态度。', 1663142755, 1663142755);
INSERT INTO `fa_event` VALUES (88, 1, '1850-02-12 00:00:00', '1850-01-01 00:00:00', '建仁风团义仓，悉捐家中长物，为族邻备荒用。', 1663142655, 1663142655);
INSERT INTO `fa_event` VALUES (90, 1, '1852-09-14 00:00:00', '1852-08-01 00:00:00', '宗棠自柳庄徙居白水洞，诛茅筑屋，亲党多从避乱', 1663146058, 1663146058);
INSERT INTO `fa_event` VALUES (91, 18, '1852-07-17 00:00:00', '1852-06-01 00:00:00', '胡林翼荐公于张亮基，左宗棠复书辞谢。亲友再三劝左宗棠出，左宗棠以保卫桑梓故，初入湘幕。', 1663146159, 1663146159);
INSERT INTO `fa_event` VALUES (92, 1, '1852-10-13 00:00:00', '1852-09-01 00:00:00', '张亮基和左宗棠一见如故。左宗棠提出几条策略，张亮基都接受了，不久将军事权都交与他，那时太平军领袖天王洪秀全从郴州率大军已到长沙，围攻很急，左宗棠日夜筹划防守，太平军硬攻未下，就穿地道用地雷攻城，也被击退。两个月后，洪秀全看看攻不下来，就撤围北上，长沙于是解围。', 1663146249, 1663146249);
INSERT INTO `fa_event` VALUES (93, 1, '1852-10-13 00:00:00', '1852-09-01 00:00:00', '太平军退后，他帮助张亮基整饬吏治，安定社会秩序，又平定了一起浏阳 “征义堂”“会匪”。', 1663146322, 1663146322);
INSERT INTO `fa_event` VALUES (94, 27, '1853-01-09 00:00:00', '1852-12-01 00:00:00', '以侍郎居忧归里，奉命帮办团练，这就是近代史中著名的湘军。', 1663146488, 1663146488);
INSERT INTO `fa_event` VALUES (95, 26, '1853-02-08 00:00:00', '1853-01-01 00:00:00', '调署湖广总督', 1663164650, 1663164650);
INSERT INTO `fa_event` VALUES (96, 1, '1853-02-09 00:00:00', '1853-01-02 00:00:00', '张亮基奉命调署湖广总督，左宗棠本不愿随去，但张亮基多方劝说，左宗棠因张亮基很尊敬、信任他，才勉勉强强随同到武昌，他自己说是在强迫之下去的。', 1663165404, 1663165404);
INSERT INTO `fa_event` VALUES (97, 26, '1853-10-03 00:00:00', '1853-09-01 00:00:00', '调任山东巡抚', 1663165438, 1663165516);
INSERT INTO `fa_event` VALUES (98, 1, '1853-10-04 00:00:00', '1853-09-02 00:00:00', '张亮基调任山东巡抚\r\n左宗棠借这机会离开了湖广总督幕府。他在鄂幕结识了一位幕友王柏心，王柏心曾做过林则徐的幕府', 1663165470, 1663165566);
INSERT INTO `fa_event` VALUES (99, 1, '1853-10-24 00:00:00', '1853-09-22 00:00:00', '抵湘阴县城，第二天就回到 25 公里外的白水洞家中,隐居梓木洞。湖南巡抚骆秉章数遣书币召公，公辞不出。', 1663165587, 1663165587);
INSERT INTO `fa_event` VALUES (100, 1, '1854-02-27 00:00:00', '1854-02-01 00:00:00', '公居白水洞，太平军占岳州、湘阴，扬言将入山找左宗棠。曾国藩治军衡州，贻书邀公，公辞之。骆秉章复遣使入山敦促，公念时事益棘，乃再度入湘幕。', 1663165995, 1663165995);
INSERT INTO `fa_event` VALUES (101, 1, '1854-06-25 00:00:00', '1854-06-01 00:00:00', '湘军攻克岳州，太平军离湘境，公仍欲辞归，骆秉章勉留，自是以至咸丰十年，公遂专主湖南军事。', 1663210977, 1663210977);
INSERT INTO `fa_event` VALUES (102, 27, '1855-01-18 00:00:00', '1854-12-01 00:00:00', '湘军水师败于湖口。作为湘军统帅的曾国藩虽然在靖港战败，甚至投水欲自尽', 1663211049, 1663211049);
INSERT INTO `fa_event` VALUES (103, 1, '1855-01-18 00:00:00', '1854-12-01 00:00:00', '左宗棠主张应先支援湘潭,湘潭大捷,太平军死亡万余人，战船几乎全部覆没。', 1663211105, 1663211105);
INSERT INTO `fa_event` VALUES (104, 1, '1855-01-20 00:00:00', '1854-12-03 00:00:00', '次日早晨，左宗棠听到曾国藩从靖港兵败回长，到舟中探视。左宗棠责备他说：“现在大事尚可为，不应该为一次挫败就寻死。” 曾国藩一言不发，只叫从人将纸笔拿来，将所存的火药、子弹、军械数目详细写下，嘱左宗棠代为保管。', 1663211173, 1663211173);
INSERT INTO `fa_event` VALUES (105, 27, '1854-07-25 00:00:00', '1854-07-01 00:00:00', '由岳州前线回到长沙，准备率大军出湘东征。不久曾国藩率大军北上', 1663211689, 1663211689);
INSERT INTO `fa_event` VALUES (106, 27, '1854-09-22 00:00:00', '1854-08-01 00:00:00', '攻克武昌和汉阳，连下黄州、兴国、蕲州各城，又在田家镇打了一次胜仗；', 1663211706, 1663211706);
INSERT INTO `fa_event` VALUES (107, 27, '1854-12-20 00:00:00', '1854-11-01 00:00:00', '水陆各师进薄九江。曾国藩十分得意，湘军军威大振，却露出了骄气。左宗棠觉察到湘军虽然节节得手，但 “将士之气渐骄，主帅之谋渐乱”，败象已露。便连写数信给曾国藩，劝他持军务必须慎重，不宜轻进。但曾国藩正在兴头之上，不予答复。终于于十二月率兵进攻湖口。大败于太平军损失惨重', 1663211749, 1663211749);
INSERT INTO `fa_event` VALUES (108, 1, '1854-06-25 00:00:00', '1854-06-01 00:00:00', '湖南设立了船炮局，由丁善庆、黄冕、李概等负责，左宗棠亲自主持。黄冕是位制炮专家，以前在甘肃，后又在长沙造过炮，是用熟铁造的。左宗棠自己也设计了一种劈山炮，改用铸铁制成。\r\n\r\n', 1663213412, 1663213412);
INSERT INTO `fa_event` VALUES (109, 1, '1855-03-18 00:00:00', '1855-02-01 00:00:00', '在湘推行厘税及票盐制，罢大钱和剔漕弊,重视整饬吏治。', 1663213456, 1663222995);
INSERT INTO `fa_event` VALUES (110, 18, '1855-03-18 00:00:00', '1855-02-01 00:00:00', '由贵州知府奉诏调湖北巡抚，升任按察使。', 1663222980, 1663222980);
INSERT INTO `fa_event` VALUES (111, 18, '1855-12-09 00:00:00', '1855-11-01 00:00:00', '收复了武昌和汉阳，湖南形势又一次安定下来。', 1663223100, 1663223100);
INSERT INTO `fa_event` VALUES (112, 28, '1855-03-18 00:00:00', '1855-02-01 00:00:00', '太平军复占武昌', 1663223260, 1663223271);
INSERT INTO `fa_event` VALUES (113, 1, '1855-03-18 00:00:00', '1855-02-01 00:00:00', '骆秉章和左宗棠详细调查了湖北战况后，由骆秉章上疏奏劾湖广总督杨霈，杨不久被免职，由荆州将军官文接任。', 1663223425, 1663223425);
INSERT INTO `fa_event` VALUES (114, 28, '1855-11-10 00:00:00', '1855-10-01 00:00:00', '太平军看到在湖北一时不能取胜，突然改变主攻方向，于十月转攻江西，连克瑞州（高安）、临江（清江）、新昌（宜丰）、袁州等，进图吉安。', 1663223547, 1663223547);
INSERT INTO `fa_event` VALUES (115, 27, '1855-11-11 00:00:00', '1855-10-02 00:00:00', '曾国藩困守南昌', 1663223573, 1663223573);
INSERT INTO `fa_event` VALUES (116, 1, '1855-11-12 00:00:00', '1855-10-03 00:00:00', '左宗棠向骆秉章建议说：“江西是军事要地，如果被太平军占领，那么不仅江、浙、闽、广四省都易遭到攻击，而湖南也更危险了。骆秉章听从了他的意见。\r\n左宗棠定下三路援赣之策：\r\n南路由酃县、茶陵攻吉安；\r\n中路由浏阳、醴陵攻袁州；\r\n北路由平江攻义宁（修水），直取瑞州。议遣刘长佑、萧启江、曾国荃三路援江西。', 1663223597, 1663224790);
INSERT INTO `fa_event` VALUES (117, 1, '1856-01-08 00:00:00', '1855-12-01 00:00:00', '御史宗稷辰（字涤甫）荐举全国人才，以左宗棠列为第一，说他不求荣利、通权达变，迹甚微而功甚伟，若使独当一面，必不会次于胡林翼诸人。', 1663223800, 1663223800);
INSERT INTO `fa_event` VALUES (118, 27, '1856-02-06 00:00:00', '1856-01-01 00:00:00', '曾国藩奏左宗棠接济军饷有功', 1663224604, 1663224604);
INSERT INTO `fa_event` VALUES (119, 1, '1856-07-02 00:00:00', '1856-06-01 00:00:00', '朝廷命赐以兵部郎中衔', 1663224623, 1663234831);
INSERT INTO `fa_event` VALUES (120, 1, '1856-10-08 00:00:00', '1856-09-10 00:00:00', '广东发生了亚罗号事件,英国以此发起第二次鸦片战争', 1663224744, 1663224829);
INSERT INTO `fa_event` VALUES (121, 1, '1856-10-23 00:00:00', '1856-09-25 00:00:00', '第二次鸦片战争开始,英军开始行动，三天之内，连占虎门口内各炮台。27日，英舰炮轰广州城。29日，英军攻入城内，抢掠广州督署后退出。', 1663224888, 1663234885);
INSERT INTO `fa_event` VALUES (122, 28, '1856-08-30 00:00:00', '1856-08-01 00:00:00', '太平天国内部发生大变乱、大屠杀，洪秀全联合韦昌辉在天京屠杀杨秀清及其党二万余人，旋又杀韦昌辉。不久，石达开亦离去。', 1663224924, 1663224924);
INSERT INTO `fa_event` VALUES (123, 18, '1857-01-26 00:00:00', '1857-01-01 00:00:00', '胡林翼与骆秉章醵金五百两，在北城司马桥买下了一所住宅，送给左宗棠。这所宅子靠近郊区，以前是宋代爱国词人辛弃疾任湖南安抚使时的练兵故地', 1663224996, 1663225904);
INSERT INTO `fa_event` VALUES (124, 25, '1857-05-23 00:00:00', '1857-05-01 00:00:00', '咸丰帝下了一道上谕给骆秉章，准备调他帮助曾国藩办理军务，但骆秉章舍不得放他，以湖南军事方急，奏留了他。后来又奏他运筹之功，被诏赏加四品卿衔。', 1663226015, 1663226031);
INSERT INTO `fa_event` VALUES (125, 1, '1857-12-16 00:00:00', '1857-11-01 00:00:00', '英法军舰联合驶进珠江口，占领广州，俘虏了包括总督叶名琛在内的全部清朝大员。成立了一个以广东巡抚柏贵为首的傀儡政府,左宗棠十分关心，也很焦急。他当时已意识到英法舰队可能北上，对京津进行威胁，写信给胡林翼，对广州战役战败、政府屈辱投降的表现十分愤慨，他提出派重兵驻守天津，抗击敌人入侵，不失为一条很好的策略。', 1663226306, 1663226306);
INSERT INTO `fa_event` VALUES (126, 1, '1858-02-14 00:00:00', '1858-01-01 00:00:00', '提出抗拒英法策略：直捣香港巢穴，并派重兵防守天津。但未被采纳。', 1663226805, 1663226805);
INSERT INTO `fa_event` VALUES (127, 1, '1858-06-13 00:00:00', '1858-05-03 00:00:00', '第一次大沽口之战,英法军舰攻占大沽炮台，强迫签订中英、中法《天津条约》。', 1663226827, 1663235787);
INSERT INTO `fa_event` VALUES (128, 1, '1858-07-11 00:00:00', '1858-06-01 00:00:00', ' 签订中俄瑷珲条约,失去黑龙江以北 60 万平方公里', 1663227529, 1663227529);
INSERT INTO `fa_event` VALUES (129, 28, '1858-09-07 00:00:00', '1858-08-01 00:00:00', '太平王朝在浦口击破江北大营，歼清军万余人', 1663227573, 1663227573);
INSERT INTO `fa_event` VALUES (130, 30, '1859-03-05 00:00:00', '1859-02-01 00:00:00', '石达开大部队进入湖南，声势浩大，接连攻陷宜章、兴宁、郴州、桂阳州等地，湖南大为震动。', 1663228899, 1663228899);
INSERT INTO `fa_event` VALUES (131, 1, '1859-06-01 00:00:00', '1859-05-01 00:00:00', '五六月间，湖南重镇宝庆（今邵阳）被石达开三十万大军重重包围。左宗棠得到围城守军送出来的一份 “敌我兵势地图”，他将图挂在墙上，早晚详细研究。左宗棠从兵势分析，认为东面敌人兵力雄厚，北面薄弱，应从北面进攻。争论了两天，最后采取了左宗棠的意见。湘军由宝庆北面的新化进兵，获得大胜。石达开军败退到东安，由东安又进入广西兴安，湖南于是再度解围。', 1663229284, 1663229284);
INSERT INTO `fa_event` VALUES (132, 30, '1859-07-30 00:00:00', '1859-07-01 00:00:00', '石达开军败退到东安，由东安又进入广西兴安', 1663229308, 1663229308);
INSERT INTO `fa_event` VALUES (133, 25, '1858-04-14 00:00:00', '1858-03-01 00:00:00', '骆秉章到北京见皇帝时，告了樊燮一状，后来查实参奏，奉旨将樊燮革职查办。', 1663229517, 1663229517);
INSERT INTO `fa_event` VALUES (134, 1, '1859-12-24 00:00:00', '1859-12-01 00:00:00', '樊燮事件,湖广总督总督官文是骆秉章顶头上司，过去二人有矛盾，因此想借樊燮告状的机会，压一压汉人的气焰。\r\n因此官文接受了樊燮控告，立即奏报朝廷，说是永州知府黄文琛等与湖南巡抚幕客劣绅左宗棠，商同陷害总兵樊燮。就下旨将本案交官文和湖北正考官钱宝青审办，密旨中说：“如果左宗棠确有不法情事，可即就地正法。”', 1663230155, 1663230451);
INSERT INTO `fa_event` VALUES (135, 31, '1859-12-25 00:00:00', '1859-12-02 00:00:00', '郭嵩焘赶忙找了和他同在南书房值班的少詹潘祖荫，请他给皇上保荐奏疏，并对潘祖荫说：“如果把左宗棠搞走了，湖南就会垮台，东南大局也就完了。”，潘祖荫于是给皇帝上了一疏，说：“湖南军队不仅保住了本省，还支援了江西、湖北、广西、贵州，所向克捷。固然是骆秉章调度有方，实则由左宗棠运筹决胜。此天下所共见，也在皇上圣明洞鉴之中。” 接着他说了两句传诵一时的话：“国家不可一日无湖南，湖南不可一日无左宗棠。” 咸丰帝采纳了他的意见。', 1663230438, 1663230438);
INSERT INTO `fa_event` VALUES (136, 1, '1860-01-23 00:00:00', '1860-01-01 00:00:00', '他向骆秉章提出要参加会试，以为摆脱幕府的借口。自长沙启行赴京参加第四次会试。左宗棠的名气已很大，皇帝也点过他几次名。京师的考官们都互相告诫，“这次务必要取中左宗棠”', 1663230600, 1663230870);
INSERT INTO `fa_event` VALUES (137, 18, '1860-03-24 00:00:00', '1860-03-03 00:00:00', '派了专差送来一封密信，说：“现在攻讦诬陷你的人并不甘心，网罗四布。鄂帅（官文）正在策划构陷的办法，京师谣诼纷纭，千万不能去，以免陷入罗网。”', 1663230703, 1663230703);
INSERT INTO `fa_event` VALUES (138, 1, '1860-03-22 00:00:00', '1860-03-01 00:00:00', '到襄阳，风雪未止。', 1663230759, 1663230759);
INSERT INTO `fa_event` VALUES (139, 1, '1860-04-16 00:00:00', '1860-03-26 00:00:00', '到宿松,曾国藩，胡林翼，李鸿章、李瀚章（字筱泉）、李次青（名元度）、曾国荃等也都来到宿松，他们在一起畅谈天下大势，尤以曾、左、胡三人在一处聚谈最多，往往从早饭后一直谈到深夜。这次宿松会晤十分重要，关系到湘军今后发展动向，自然也关系他们个人和国家的前途，以及清朝的命运。\r\n\r\n\r\n', 1663230821, 1663231260);
INSERT INTO `fa_event` VALUES (141, 1, '1860-06-19 00:00:00', '1860-05-01 00:00:00', '接奉诏书，以四品京堂候补，随曾国藩襄办军务。他随即在长沙招募了五千人，成立了楚军。楚军总共 5000 余人，从此，他就打消了隐居山林的念头，告别了家乡，率领楚军东征西讨，开始了几十年的戎马生涯。', 1663232661, 1663232661);
INSERT INTO `fa_event` VALUES (140, 28, '1860-03-22 00:00:00', '1860-03-01 00:00:00', '击破江南大营，歼敌数万人，清军主帅和春自杀。太平军重振了威势，连克苏州、常州，进入浙江，攻陷嘉兴。', 1663231252, 1663231252);
INSERT INTO `fa_event` VALUES (142, 1, '1860-08-17 00:00:00', '1860-07-01 00:00:00', '楚军东征,从长沙出发，经醴陵进入江西，原想去祁门与曾国藩军会合。半路上听到太平军已占领徽州，于是左宗棠率部队由南昌经过乐平，进驻江西东北门户景德镇。太平军别部由广东进入赣南，准备进占景德镇。左宗棠派王开琳的老湘营击退了它。这支别部就逃往景德镇东南的德兴，左宗棠派王开化、杨昌濬率部队在半路截击，王开琳的老湘营又从后追来，太平军大败而逃，楚军乘胜收复了德兴，，两天后收复了婺源，太平军逃往浙江。楚军出师首战告捷，收复两要地，在江西站稳了脚跟。这也是左宗棠的第一次实战。', 1663232771, 1663232771);
INSERT INTO `fa_event` VALUES (143, 1, '1856-12-01 00:00:00', '1856-11-04 00:00:00', '第二次鸦片战争:洋行夷馆被毁，尽成灰烬。一艘自广州开往香港的英国邮船遭劫。', 1663234929, 1663234929);
INSERT INTO `fa_event` VALUES (144, 1, '1857-01-26 00:00:00', '1857-01-01 00:00:00', '第二次鸦片战争:英军焚烧洋行附近民宅数千家，后因兵力不足，为等待援军而退出珠江内河。', 1663234951, 1663235481);
INSERT INTO `fa_event` VALUES (145, 1, '1859-06-01 00:00:00', '1859-05-01 00:00:00', '英、法政府远不满足从《天津条约》攫取的种种特权，蓄意利用换约之机挑起第二次大沽口之战', 1663235329, 1663235329);
INSERT INTO `fa_event` VALUES (146, 1, '1860-04-01 00:00:00', '1860-03-11 00:00:00', '英、法帝国主义当局分别再度任命额尔金和葛罗为全权代表，率领英军一万五千余人，法军约七千人，扩大侵华战争。4月，英法联军占领舟山。5、6月，英侵略军占大连湾，法侵略军占烟台，封锁渤海湾', 1663236813, 1663236813);
INSERT INTO `fa_event` VALUES (147, 1, '1860-08-01 00:00:00', '1860-06-15 00:00:00', '英法联军18000人，由北塘登陆，进占天津。14日，攻陷塘沽，水陆协同进攻大沽北岸炮台。21日，大沽失陷。侵略军长驱直入，24日占领天津。', 1663236892, 1663236892);
INSERT INTO `fa_event` VALUES (148, 1, '1860-09-18 00:00:00', '1860-08-04 00:00:00', '英法侵略军攻陷通州。21日，清军与英法联军在八里桥展开激战，统帅僧格林沁等率先逃走，清军全军覆没。', 1663236958, 1663236958);
INSERT INTO `fa_event` VALUES (149, 29, '1860-09-22 00:00:00', '1860-08-08 00:00:00', '咸丰帝等则以北狩为名携皇后、懿贵妃等离京逃往热河避暑山庄。', 1663237031, 1663237031);
INSERT INTO `fa_event` VALUES (150, 1, '1860-10-13 00:00:00', '1860-08-29 00:00:00', '联军从安定门攻入北京。联军发现清军将英法使节团多人虐待至死，遂决定报复中国之野蛮行为，及教训清朝皇室以后不得蔑视英国及法国。', 1663237064, 1663237064);
INSERT INTO `fa_event` VALUES (151, 1, '1860-10-18 00:00:00', '1860-09-05 00:00:00', '英法联军占领北京，抢劫焚毁圆明园。英法联军在北京城郊抢掠烧杀近50天', 1663237091, 1663237091);
INSERT INTO `fa_event` VALUES (152, 1, '1860-10-25 00:00:00', '1860-09-12 00:00:00', '中法《北京条约》', 1663237169, 1663237169);
INSERT INTO `fa_event` VALUES (153, 1, '1861-04-10 00:00:00', '1861-03-01 00:00:00', '击败太平军于乐平，李世贤潜遁。', 1663249283, 1663249283);
INSERT INTO `fa_event` VALUES (154, 27, '1861-05-10 00:00:00', '1861-04-01 00:00:00', '在徽州却打了败仗，溃退到休宁。', 1663249803, 1663249848);
INSERT INTO `fa_event` VALUES (155, 1, '1861-07-08 00:00:00', '1861-06-01 00:00:00', '视察婺源', 1663249821, 1663249837);
INSERT INTO `fa_event` VALUES (156, 29, '1861-08-20 00:00:00', '1861-07-15 00:00:00', '病逝于承德行宫,改元同治，慈安、慈禧两宫太后垂帘听政，实际则由同治帝生母慈禧掌权。不久就发生了一次宫廷政变：由于以肃顺为首的八大臣反对太后听政，分别被处死、流放、免职，而由道光帝第六子恭亲王奕当政。', 1663249885, 1663249901);
INSERT INTO `fa_event` VALUES (157, 27, '1861-09-05 00:00:00', '1861-08-01 00:00:00', '攻下安庆', 1663249924, 1663249924);
INSERT INTO `fa_event` VALUES (158, 18, '1861-09-30 00:00:00', '1861-08-26 00:00:00', '在武昌病逝。他病危时致信给左宗棠，与他诀别，勉励他和曾国藩继续未竟的事业。胡林翼显然是患了肺结核或肺癌，这在当时都是不治之症。', 1663249975, 1663249975);
INSERT INTO `fa_event` VALUES (159, 32, '1861-12-02 00:00:00', '1861-11-01 00:00:00', '辛酉政变,由于以肃顺为首的八大臣反对太后听政，分别被处死、流放、免职，而由道光帝第六子恭亲王奕当政。', 1663250127, 1663250127);
INSERT INTO `fa_event` VALUES (160, 28, '1861-12-29 00:00:00', '1861-11-28 00:00:00', '攻陷杭州城,巡抚王有龄自缢身死', 1663250367, 1663250435);
INSERT INTO `fa_event` VALUES (161, 1, '1861-12-27 00:00:00', '1861-11-26 00:00:00', '清朝廷诏命左宗棠赴浙督办军务。', 1663250424, 1663250424);
INSERT INTO `fa_event` VALUES (162, 27, '1861-12-30 00:00:00', '1861-11-29 00:00:00', '曾国藩密疏荐左宗棠为浙江巡抚，并写信给左宗棠说：“目下经营浙事，全仗大力，责无旁贷。”', 1663250478, 1663250478);
INSERT INTO `fa_event` VALUES (163, 1, '1861-12-31 00:00:00', '1861-12-01 00:00:00', '朝廷下旨任命左宗棠为浙江巡抚。', 1663250500, 1663250500);
INSERT INTO `fa_event` VALUES (164, 1, '1862-01-30 00:00:00', '1862-01-01 00:00:00', '楚军进入浙江，收复了浙西的开化，太平军退到遂安、常山一带。', 1663250879, 1663250879);
INSERT INTO `fa_event` VALUES (165, 1, '1862-01-30 00:00:00', '1862-01-01 00:00:00', '收复遂安，太平军损失很大', 1663250905, 1663250905);
INSERT INTO `fa_event` VALUES (166, 1, '1862-03-30 00:00:00', '1862-03-01 00:00:00', '抵达江山，在清湖击败李世贤军，李世贤本人也几乎被抓获。', 1663250947, 1663250947);
INSERT INTO `fa_event` VALUES (167, 1, '1862-06-27 00:00:00', '1862-06-01 00:00:00', '楚军进抵衢州城外，刘典、杨昌濬部队攻城北，刘培元、王德榜攻城东南，太平军败退，衢州解严。', 1663250977, 1663250977);
INSERT INTO `fa_event` VALUES (168, 1, '1862-12-21 00:00:00', '1862-11-01 00:00:00', '部将魏喻义攻克严州', 1663251147, 1663251147);
INSERT INTO `fa_event` VALUES (169, 1, '1862-12-21 00:00:00', '1862-11-01 00:00:00', '以法兵为主的洋兵进入绍兴城后，干了许多坏事，左宗棠正式提出遣撤洋兵,左谕知他：“中国剿贼并不需要借助外力。” 和他订了条约存案：须听左宗棠约束，不许节外生枝，兵员不能增加。德克碑表示一一接受', 1663251184, 1663251198);
INSERT INTO `fa_event` VALUES (170, 10, '1862-06-27 00:00:00', '1862-06-01 00:00:00', '入县学，又中举人。', 1663251284, 1663251284);
INSERT INTO `fa_event` VALUES (171, 1, '1863-02-18 00:00:00', '1863-01-01 00:00:00', '在严州得了一场疟疾，一天之中忽冷忽热，苦恼不堪。患了慢性肠胃炎（长期腹泻），仅靠服点补药调养，效果也不好。', 1663251321, 1663251321);
INSERT INTO `fa_event` VALUES (172, 1, '1863-05-18 00:00:00', '1863-04-01 00:00:00', '清廷任命左宗棠为闽浙总督仍兼浙江巡抚', 1663251336, 1663251336);
INSERT INTO `fa_event` VALUES (173, 1, '1863-09-19 00:00:00', '1863-08-07 00:00:00', '楚军大举进攻富阳，初七深夜攻破富阳城。十二日，魏喻义、康国器两军由新城趋余杭，二十五日，高连升部攻破杭州清波门太平军堡垒，进驻万松岭。', 1663251407, 1663251407);
INSERT INTO `fa_event` VALUES (174, 1, '1863-12-11 00:00:00', '1863-11-01 00:00:00', '由严州进驻富阳，并到余杭前线视察。', 1663251641, 1663251641);
INSERT INTO `fa_event` VALUES (175, 1, '1864-01-10 00:00:00', '1863-12-02 00:00:00', '包围杭州,高连升军攻杭州凤山门，连破九个堡垒，水师也攻破了江边石垒。初二日，大军逼城，将四门困住。余杭城也被黄少春、杨昌濬等部队将北、东、西北三面包围，留了一个缺口。', 1663251711, 1663251711);
INSERT INTO `fa_event` VALUES (176, 1, '1864-03-08 00:00:00', '1864-02-01 00:00:00', '二月，攻克杭州，加太子少保衔，赏穿黄马褂。将士也都分别升官。进城后办的第一件事，就是 设立赈抚局，收养难民，对孤寡妇女儿童都妥善安置。对暴露在街头野外的尸骨，鼓励人民掩埋，给以报酬。又定了几条严禁：严禁军士擅入民居，凡兵勇扰害闾门者斩，拐掠贩卖人口者斩，扰索诈胁绅民者判罪；禁止屠杀耕牛，以保护农业生产。他带兵作战所过之地，虽然饷粮困乏，仍命官兵发散钱米或煮粥分给穷苦人民。浙江富绅杨坊、毛象贤、俞斌等为富不仁。左宗棠上奏弹劾浙江富绅，责令他们筹集巨资，去各地购粮，运回浙江办赈。他还采取以工代赈的办法，修理城墙、衙署等，并教妇女纺织。\r\n为了减轻人民负担，他办了几件事： 这几件兴利除弊的事，多是左宗棠以前在湖南办过的，他已积累了经验，驾轻就熟。', 1663293819, 1663293819);
INSERT INTO `fa_event` VALUES (177, 33, '1864-07-04 00:00:00', '1864-06-01 00:00:00', '攻陷天京', 1663297642, 1663297642);
INSERT INTO `fa_event` VALUES (178, 1, '1864-10-01 00:00:00', '1864-09-01 00:00:00', '封一等伯爵。仿造小轮船，试行于西湖。然质量还不怎么好，但却是我国自制的第一艘机器轮船。', 1663298352, 1663298558);
INSERT INTO `fa_event` VALUES (179, 1, '1864-07-04 00:00:00', '1864-06-01 00:00:00', '左宗棠针对这种百废待兴的局面，实行了几项善后措施：一是招商开市，并减低厘税，使市场逐渐繁荣起来。农民逃亡的很多，就广招邻省农民来浙，贷给耕牛和种子。种地的人仍然不够，就命军士就地开垦。荒废多年的田经军士耕种好，或到了收获季节，原主来认领了，命军士一律归还，不许留难。劫后余生的农民对这些措施十分感动。当部队拔营时，农民们包括儿童妇女都 “环拜泣谢”。\r\n左宗棠看到浙江蚕桑之利，就教农民种桑、养蚕，还教种棉花。他说：“教种桑棉，为养民务本之要。”', 1663298510, 1663298510);
INSERT INTO `fa_event` VALUES (180, 1, '1864-10-09 00:00:00', '1864-09-09 00:00:00', '杨昌濬的寿辰，又恰好是重阳节，大家同到灵隐、天竺一游。', 1663298617, 1663298617);
INSERT INTO `fa_event` VALUES (181, 23, '1844-02-18 00:00:00', '1844-01-01 00:00:00', '考上进士', 1663298748, 1663298748);
INSERT INTO `fa_event` VALUES (182, 1, '1864-07-04 00:00:00', '1864-06-01 00:00:00', '自己花钱替魏源修墓', 1663298821, 1663298821);
INSERT INTO `fa_event` VALUES (183, 1, '1864-11-26 00:00:00', '1864-10-28 00:00:00', '因为福建军情紧急，朝廷命左宗棠速去福州履总督任。由杭州起行，途经金华、衢州赴闽。', 1663312613, 1663312613);
INSERT INTO `fa_event` VALUES (184, 1, '1864-07-04 00:00:00', '1864-06-01 00:00:00', '与曾国藩“失和”,天京攻陷后，曾国藩向朝廷奏报克复金陵，所有悍贼被一网打尽，并特别指出，城破后，伪幼主积薪于宫中，举火自焚。这就凸现了曾国藩对太平军有攻克全功。不想左宗棠也上一折，称据金陵逃出难民供出伪幼主洪天贵福于同治六月二十一日由东坝逃至广德，被太平军将领黄文金迎入湖州府城，想借伪幼主名号，召集太平军余众。清政府看到左宗棠奏报后，对曾国藩大为不满。曾国藩平生自认为以诚信为本，假如按左宗棠所言，则无异于欺君罔上。于是曾国藩上折反驳左宗棠，称洪天贵福可能已死而黄文金为纠合太平军余众伪称尚存，这是古来常有之事等。言外之意，左宗棠虚张声势，不过是邀功请赏。左宗棠看到此奏后，又上书为自己辩解，对曾国藩言词激烈，口诛笔伐。至此曾左二人的关系已不可挽回，十几年的交情为了各自的名利而付诸东流。', 1663312968, 1663312968);
INSERT INTO `fa_event` VALUES (185, 27, '1865-07-23 00:00:00', '1865-06-01 00:00:00', '太平军战事全部平息。立即交出兵权,慈禧对湘军也不再猜忌，认为汉人无足畏了。', 1663313053, 1663313053);
INSERT INTO `fa_event` VALUES (186, 1, '1865-04-25 00:00:00', '1865-04-01 00:00:00', '到福州,太平军战事全部平息。奏请在福建改行票盐。', 1663313098, 1663313098);
INSERT INTO `fa_event` VALUES (187, 27, '1865-07-24 00:00:00', '1865-06-02 00:00:00', '封为一等侯爵、太子太保', 1663313135, 1663313135);
INSERT INTO `fa_event` VALUES (188, 33, '1865-09-21 00:00:00', '1865-08-02 00:00:00', '封为一等伯爵、太子少保', 1663313158, 1663313158);
INSERT INTO `fa_event` VALUES (189, 1, '1865-07-24 00:00:00', '1865-06-02 00:00:00', '封为一等伯爵、太子少保', 1663313173, 1663313173);
INSERT INTO `fa_event` VALUES (190, 1, '1865-10-20 00:00:00', '1865-09-01 00:00:00', '江南制造局成立 中国最大军工厂', 1663313205, 1663313205);
INSERT INTO `fa_event` VALUES (191, 1, '1866-06-13 00:00:00', '1866-05-01 00:00:00', '左宗棠才将周夫人等全家接来\r\n两人已分别六年了。他和周夫人相见之下，眼泪止不住滚滚流下，英雄气短，半晌说不出话来。他孤单在外日久，见到夫人、儿子、媳妇，还添了孙子，又欢聚一堂，心中愉悦不可言喻。', 1663313609, 1663313609);
INSERT INTO `fa_event` VALUES (192, 1, '1866-03-17 00:00:00', '1866-02-01 00:00:00', '回到福州办大事,\r\n一）开设正谊堂书局。\r\n二）修建芝山和正谊书院。\r\n三）改革盐政，裁革陋规。\r\n四）整饬兵治。\r\n五）经营台湾。\r\n六）在福州还遇到一件涉外事件\r\n七)  创办福州船政局\r\n八)  开蚕棉馆,又命各州县积谷备荒', 1663313680, 1663313735);
INSERT INTO `fa_event` VALUES (193, 1, '1866-06-13 00:00:00', '1866-05-01 00:00:00', '奏请自造轮船，为强国富民之计。设福州船政局，择马尾山下为造船厂址。', 1663313771, 1663313771);
INSERT INTO `fa_event` VALUES (194, 1, '1866-10-09 00:00:00', '1866-09-01 00:00:00', '调任陕甘总督，处理回事。奏请以沈葆桢为船政大臣，遣人赴洋购造船机器，设求是堂艺局（船政学堂）。', 1663313834, 1663313834);
INSERT INTO `fa_event` VALUES (195, 1, '1866-11-07 00:00:00', '1866-10-01 00:00:00', '离开福州', 1663313861, 1663313861);
INSERT INTO `fa_event` VALUES (196, 1, '1867-01-06 00:00:00', '1866-12-01 00:00:00', '抵达湖北武昌，在行军途中，左宗棠接到诏书，因为捻军首领张宗禹已进入陕西，改命他先去西安追堵捻军。在汉口，他一面等待由各地调集的军队，同时研究进兵策略。他向清廷提出了用兵程序：\r\n欲靖西陲，必先清腹地，然后客军无后顾之忧，饷道免中梗之患。他还特地拜访了老友王柏心，听取他对新疆用兵的意见。\r\n左宗棠后来平定新疆时采取的一项成功的战略 ——“缓进急战”\r\n清朝廷命李鸿章为剿捻主帅，并督剿东捻，左宗棠负责征剿西捻。\r\n\r\n', 1663313885, 1663314048);
INSERT INTO `fa_event` VALUES (197, 8, '1867-01-07 00:00:00', '1866-12-02 00:00:00', '左宗植特地带着幼子浑（丁叟）从长沙赶来探望。兄弟二人分别已有七年了。兄弟两最后一面', 1663314131, 1663314197);
INSERT INTO `fa_event` VALUES (198, 3, '1867-01-08 00:00:00', '1866-12-03 00:00:00', '年初，周夫人率家人至闽，年末由海道还湖南，路过汉口与左宗棠相遇', 1663314171, 1663317463);
INSERT INTO `fa_event` VALUES (199, 1, '1867-03-06 00:00:00', '1867-02-01 00:00:00', '离开汉口,转战中原', 1663317548, 1663317548);
INSERT INTO `fa_event` VALUES (200, 1, '1867-07-02 00:00:00', '1867-06-01 00:00:00', '抵达潼关', 1663317585, 1663317585);
INSERT INTO `fa_event` VALUES (201, 1, '1867-11-26 00:00:00', '1867-11-01 00:00:00', '西捻军渡河进山西', 1663317620, 1663317638);
INSERT INTO `fa_event` VALUES (202, 34, '1867-12-26 00:00:00', '1867-12-01 00:00:00', '平东捻。', 1663317812, 1663317812);
INSERT INTO `fa_event` VALUES (203, 3, '1867-11-26 00:00:00', '1867-11-01 00:00:00', '周夫人这次患的是脚气病，同治六年曾大发作。医生看了后，说：“脉绝，不可救了。” 但是服了参茸补剂后，病情好转。', 1663317845, 1663317939);
INSERT INTO `fa_event` VALUES (204, 10, '1867-12-26 00:00:00', '1867-12-01 00:00:00', '年底再次从长沙出发，去京会试。刚走了没有多久，周夫人的脚气病发作起来，几天后病势危重。左宗植赶紧派人去追回他，隔了几天周夫人病情好转，他还在半途，宗植又派人去通知他，继续去京安心会试，不用回家了。\r\n宗棠当时正在河北各地与西捻军作战，得到孝威信，知道他已北上，立即派专人送信回湘，嘱令孝威回家，并责怪周夫人，不该让他去京。不久孝威已抵达北京，准备参加会试了，宗棠正在望都行营，立即写信给孝威，狠狠批评了他一番，说：“天下哪有父亲处于战争危险之地、母亲卧病在床，儿子却从从容容参加考试的道理？你断不准入闱考试！”', 1663318012, 1663318012);
INSERT INTO `fa_event` VALUES (205, 1, '1868-01-25 00:00:00', '1868-01-01 00:00:00', '西捻军逼近京郊，京畿震动，左宗棠及李鸿章、直隶总督官文、河南巡抚李鹤年均夺职。', 1663554126, 1663554126);
INSERT INTO `fa_event` VALUES (206, 1, '1868-04-23 00:00:00', '1868-04-01 00:00:00', '西捻军越吴桥，犯天津', 1663554143, 1663554181);
INSERT INTO `fa_event` VALUES (207, 1, '1868-07-20 00:00:00', '1868-06-01 00:00:00', '张宗禹带领残部由山东济阳向西，往临邑进发，官军从四面包围，走到徒骇河边，下起大雨，河水猛涨，淮军刘铭传部将捻军驱赶到河边，捻军无路可退，又无法渡河，张宗禹遂蹈水而死。余部投降的投降，溃散的溃散，捻军就此全部被消灭了。\r\n升任太子太保衔。', 1663554237, 1663554237);
INSERT INTO `fa_event` VALUES (208, 1, '1868-09-20 00:00:00', '1868-08-05 00:00:00', '八月初五日左宗棠到达天津，初十日到北京。\r\n八月十五日，宗棠觐见了慈安、慈禧两宫皇太后。他当了七年的巡抚、总督，和朝廷奏折来往频繁，但见皇太后和皇上还是第一次。太后仍害怕陕甘回军打过来,太后又问：“西北何时可以平定？” 宗棠考应声说：“需要五年。”', 1663555192, 1663555199);
INSERT INTO `fa_event` VALUES (209, 1, '1869-01-13 00:00:00', '1868-12-01 00:00:00', '陕北反清首领董福祥等降清。', 1663555226, 1663555226);
INSERT INTO `fa_event` VALUES (210, 1, '1869-03-13 00:00:00', '1869-02-01 00:00:00', '清军攻占陇东董志原回军城堡及庆阳府城。', 1663556818, 1663556818);
INSERT INTO `fa_event` VALUES (211, 1, '1869-06-10 00:00:00', '1869-05-01 00:00:00', '命刘松山、魏光焘等分北、中、南三路进击回军。', 1663556834, 1663556834);
INSERT INTO `fa_event` VALUES (212, 35, '1869-09-06 00:00:00', '1869-08-01 00:00:00', '刘松山部进逼金积堡。', 1663556848, 1663557157);
INSERT INTO `fa_event` VALUES (213, 1, '1869-10-05 00:00:00', '1869-09-01 00:00:00', '奏陈处理回事政策：剿抚兼施，以抚为先。回汉一视同仁，不分汉回，只分良莠。', 1663556870, 1663556870);
INSERT INTO `fa_event` VALUES (214, 1, '1869-07-18 00:00:00', '1869-06-10 00:00:00', '福州轮船局,第一艘国产轮船 “万年青号” 下水了，排水量为 1370 吨，由中国舵工和水手驾驶。', 1663556919, 1663556919);
INSERT INTO `fa_event` VALUES (215, 35, '1870-01-31 00:00:00', '1870-01-01 00:00:00', '率军围攻金积堡。一次在附近马王寨前亲自督战，被寨前飞炮击中左胸。于当日伤重死去。左宗棠丧失了一员大将，就命刘松山的侄儿刘锦棠接管老湘军。\r\n\r\n', 1663557263, 1663557263);
INSERT INTO `fa_event` VALUES (216, 27, '1870-05-30 00:00:00', '1870-05-01 00:00:00', '天津发生了多起拐骗儿童的事件，还发现幼儿幼女的尸体，城中人心惶惶，六月十八日，天津民团盘查到一名拐犯武兰珍，武犯供认是受法国教堂王三指示，麻药也是王三供给的。由于牵涉外国人，清官府不敢轻率从事，由道员周家勋、知府张光藻、知县刘杰三位地方主管，带领众人押解武兰珍到天主堂，找王三对质。本来群众殴打不过是件小事，可是法国驻津领事丰大业却以官方未认真弹压为借口，小题大做，竟携带两杆洋枪，和一批随从手持利刃，直闯进三口通商大臣崇厚的衙门。找到崇厚后，就开枪射击，幸而崇厚躲得快，逃进了内室，才幸免于难。丰大业这个帝国主义分子咆哮了一阵，将官署内的什物文件捣毁一空，才悻悻离开衙门。半路上正遇到从天主堂转回的刘杰，丰大业又向刘杰开枪，打伤了一名家丁。围观的群众怒不可遏，忍无可忍，哪能容许帝国主义分子在中国街道上任意开枪杀人？群众一哄而上，乱拳之下，将丰大业击毙。天津人民和全中国人民一样，受外国人压迫好几十年，憋了一肚子气，这时突然发泄出来。一些市民立即鸣锣，把全城人民聚集起来，焚毁教堂和一些外国人的房屋，打死十余名法国人、三名俄国人，以及一些中国教民。这样事情就闹大了。事件发生后，京畿和外省都受到震动。首先是法国公使向清政府提出抗议，并以武力威胁，随之英、美、俄、意等国也联合抗议，并出动军舰在沿海巡游示威。', 1663558949, 1663559069);
INSERT INTO `fa_event` VALUES (217, 1, '1870-09-25 00:00:00', '1870-09-01 00:00:00', '金积堡合围', 1663559128, 1663559128);
INSERT INTO `fa_event` VALUES (218, 37, '1870-10-24 00:00:00', '1870-10-01 00:00:00', '侵占吐鲁番地区及乌鲁木齐，新疆全境尽失。阿古柏在各城征收地税，并强令回汉各族人民改变衣着和风俗，仿效安集延国，于是新疆全境都沦陷了。', 1663559288, 1663559834);
INSERT INTO `fa_event` VALUES (219, 38, '1871-01-21 00:00:00', '1870-12-01 00:00:00', '清代同治陝甘回變的主要領導人投降', 1663559445, 1663559451);
INSERT INTO `fa_event` VALUES (220, 27, '1870-06-29 00:00:00', '1870-06-01 00:00:00', '主持总理衙门的恭亲王奕，他深恐得罪外国人,特委派德高望重的直隶总督曾国藩,立即由保定前往天津处理教案。这是曾国藩一生中所办的最后一件大事，也是他办的唯一的一次重大外交事件。曾国藩于接得赴津谕旨后，诚惶诚恐，心情十分复杂。他此去可能凶多吉少，因此写信给纪泽、纪鸿两个儿子，作为遗嘱，预先安排后事', 1663559522, 1663559522);
INSERT INTO `fa_event` VALUES (221, 27, '1870-09-26 00:00:00', '1870-08-31 00:00:00', '天津教案结案 左宗棠大骂曾国藩\r\n最后结案共得正法之犯 20 人（后来实际处死 17 人），军徒各犯 25 人。张光藻、刘杰发往黑龙江充军，另赔偿法国 46 万两银、俄国 3 万两。曾国藩自认比较满意，说：“办理不为不重，不惟足对法国，亦堪遍告诸邦。”', 1663559563, 1663559563);
INSERT INTO `fa_event` VALUES (222, 1, '1870-04-01 00:00:00', '1870-03-01 00:00:00', '才得到周夫人的噩耗，当时正在甘肃平军次。他悲不可抑地写信给儿子们，仔细地安排了周夫人的后事。他叮嘱丧事不要张扬，不要广散讣文，不要随便用乐队鼓吹，只在祭奠时才奏乐；题主不必请名人，由侄儿丁叟或女婿陶桄即可，他二人字都写得很好。不必做佛事，长沙城中灾民、乞丐多，出殡之日多散给钱财，比布施给和尚胜过十倍。觅地不讲求风水，只须避白蚁之害；买地决不可用势欺压；做冢用三合土，可避白蚁和树根之患；在周夫人墓穴旁，要留下一个墓穴，以备他去世之后安葬。在周夫人生前，他已谈过，死后同穴而眠。他还一再叮嘱：丧事不可铺张。古语云：“孝子不俭其亲丧事。” 自然不可过于省俭，理所当用的，多用点亦无妨；但是不当用的，一文钱亦不可用。不要害怕别人议论。有人会说：瞧这样的大官家庭，却故意装出穷相，这不必理会。专讲体面，不讲道理，是他素来认为可耻的事。\r\n宗棠在极度悲痛的时刻，还将丧事考虑安排得如此周到细密。他还写了一篇《亡妻周夫人墓志铭》，写完墓志铭和信的时候，已经是午夜四鼓了，他也已止不住泪湿衣襟了。', 1663559628, 1663559628);
INSERT INTO `fa_event` VALUES (223, 3, '1870-03-03 00:00:00', '1870-02-02 00:00:00', '四女孝瑸殉夫死。周夫人素来疼爱儿女，受不了这样大的打击，肝病大发。在孝瑸去世后第七日（二月初二日）去世，享年 59 岁。\r\n。孝威素来十分孝顺父母，心里极为忧伤。他想起古代二十四孝中有 “割股疗亲” 的孝子，于是偷偷从臂上割下一块肉，和药煮了给母亲吃，以为孝心可以感动天地。这种不科学的做法当然无济于事。不久周夫人去世。宗棠后来从宗植的信中知道这事，批评了孝威。', 1663559705, 1663559705);
INSERT INTO `fa_event` VALUES (224, 1, '1871-02-19 00:00:00', '1871-01-01 00:00:00', '处死马化漋父子', 1663559859, 1663559859);
INSERT INTO `fa_event` VALUES (225, 1, '1871-04-20 00:00:00', '1871-03-01 00:00:00', '俄军占领伊犁地区。', 1663572770, 1663572794);
INSERT INTO `fa_event` VALUES (226, 1, '1871-05-19 00:00:00', '1871-04-01 00:00:00', '在西安设书局，刊刻经籍。', 1663572786, 1663572786);
INSERT INTO `fa_event` VALUES (227, 1, '1871-07-18 00:00:00', '1871-06-01 00:00:00', '清军分三路进攻河州回军', 1663572833, 1663572864);
INSERT INTO `fa_event` VALUES (228, 1, '1871-08-16 00:00:00', '1871-07-01 00:00:00', '函刘锦棠速销假回甘，准备出关对付俄国。是年，设兰州制造局，制造枪炮弹药，以总兵赖长主其事。', 1663572850, 1663572903);
INSERT INTO `fa_event` VALUES (229, 1, '1871-09-15 00:00:00', '1871-08-01 00:00:00', '大军渡过洮河', 1663572885, 1663572885);
INSERT INTO `fa_event` VALUES (230, 1, '1871-07-18 00:00:00', '1871-06-01 00:00:00', '宗棠很想念孝威，叫他来甘肃陪伴他。那时宗棠在安定，军事很忙，孝威到军营后，宗棠有些文稿就交给他草拟，他也忙忙碌碌，不能好好休息。有一次他拟的稿子不合意，宗棠很生气，他待儿子素来严格，狠狠责备了一顿。孝威是个忠厚人，觉得自己没有办好事，心中过意不去，又气恼又着急，不料吐出几口血。宗棠于是命他回长沙休养。\r\n\r\n\r\n\r\n————————————————\r\n原文作者：竖横山\r\n转自链接：https://learnku.com/articles/71153\r\n版权声明：著作权归作者所有。商业转载请联系作者获得授权，非商业转载请保留以上作者信息和原文链接。', 1663572926, 1663572926);
INSERT INTO `fa_event` VALUES (231, 1, '1872-02-09 00:00:00', '1872-01-01 00:00:00', '河州回军首领马占鳌投降。', 1663573081, 1663573081);
INSERT INTO `fa_event` VALUES (232, 1, '1872-03-09 00:00:00', '1872-02-01 00:00:00', '徐占彪军进抵肃州城郊。', 1663573098, 1663573098);
INSERT INTO `fa_event` VALUES (233, 1, '1872-05-07 00:00:00', '1872-04-01 00:00:00', '内阁学士宋晋议撤福建船局，左宗棠复奏驳之。', 1663573170, 1663573187);
