INSERT INTO `fa_event` VALUES (235, 1, '1872-08-04 00:00:00', '1872-07-01 00:00:00', '自安定入驻兰州。于兰州修治督署后园，制机轮抽提黄河水，注园中“饮和池”，供民汲饮，为文记之。', 1663573228, 1663573288);
INSERT INTO `fa_event` VALUES (236, 36, '1872-11-01 00:00:00', '1872-10-01 00:00:00', '刘锦棠等军攻西宁，回军首领马桂源、本源逃走，清军攻克西宁。', 1663573262, 1663573262);
