INSERT INTO `fa_event` VALUES (238, 8, '1872-03-09 00:00:00', '1872-02-01 00:00:00', '左宗植三子丁叟于去京师会试，没有考中。也许是少年气盛，回到家后就患病，二月病故。', 1663573514, 1663573520);
INSERT INTO `fa_event` VALUES (239, 1, '1873-02-27 00:00:00', '1873-02-01 00:00:00', '上奏疏:“欲杜俄人狡谋，必先定回部，欲收伊犁，必先克乌鲁木齐。”', 1663573568, 1663573596);
INSERT INTO `fa_event` VALUES (240, 1, '1873-08-23 00:00:00', '1873-07-01 00:00:00', '左宗棠亲自到肃州城外巡视督战，肃州已被围困一年半了，虽然尚未被攻下，但实力已耗尽，难于支持。马文禄于是决定求抚。', 1663573584, 1663575413);
INSERT INTO `fa_event` VALUES (241, 1, '1873-10-21 00:00:00', '1873-09-01 00:00:00', '马文禄投降，清军收复肃州，打通进军新疆要道。白彦虎窜入新疆。\r\n诏授协办大学士、一等轻车都尉。白彦虎率回军余部逃到哈密，已进驻古城的景廉告急。正好是五年。', 1663573624, 1663575448);
INSERT INTO `fa_event` VALUES (242, 1, '1873-11-20 00:00:00', '1873-10-01 00:00:00', '巡视嘉峪关，返兰州。', 1663573658, 1663573658);
INSERT INTO `fa_event` VALUES (243, 15, '1873-03-11 00:00:00', '1873-02-13 00:00:00', '次女孝琪病逝，年四十岁。', 1663573725, 1663573725);
INSERT INTO `fa_event` VALUES (244, 1, '1874-01-18 00:00:00', '1873-12-01 00:00:00', '上谕：“着即出关，收复乌鲁木齐。”奏陈关塞用兵之策，在精不在多。奏请甘肃与陕西分闱乡试。是年冬，福建船工将竣，已造成舰船15艘。', 1663573783, 1663573783);
