INSERT INTO `fa_event` VALUES (247, 1, '1874-04-16 00:00:00', '1874-03-01 00:00:00', '金顺、额尔庆额军相继出关。', 1663638121, 1663638121);
INSERT INTO `fa_event` VALUES (248, 40, '1874-05-16 00:00:00', '1874-04-01 00:00:00', '日本侵入台湾，沈葆桢渡海设防。各省停解西征协饷。', 1663638939, 1663638939);
INSERT INTO `fa_event` VALUES (249, 1, '1874-08-12 00:00:00', '1874-07-01 00:00:00', '晋东阁大学士。', 1663652648, 1663652648);
INSERT INTO `fa_event` VALUES (250, 1, '1874-10-10 00:00:00', '1874-09-01 00:00:00', '总理衙门为加强海防，提出练兵、造船、筹饷等六条，广征各大臣、督抚意见。', 1663652899, 1663652899);
INSERT INTO `fa_event` VALUES (251, 34, '1874-12-09 00:00:00', '1874-11-01 00:00:00', '奏复：新疆旷地，收复不值，将来亦断不能守；宜停撤西征军，以匀作海防军饷。', 1663652929, 1663652929);
INSERT INTO `fa_event` VALUES (252, 1, '1875-03-08 00:00:00', '1875-02-01 00:00:00', '廷议海防塞防之争，密征公意见。奏对：“东则海防，西则塞防，二者并重。”坚持进军新疆，收复失地。军机大臣文祥亦力主进剿，事遂定。谕旨命公以钦差大臣督办关外剿匪事宜，金顺帮办。《复陈海防塞防及关外剿抚粮运情形折》在折中慷慨陈词，详细分析了敌我形势，一一驳斥了卖国投降派的议论。他首先就指出，对于国家的安危，“东则海防，西则塞防，二者并重”。而当时形势，塞防比海防更急迫。', 1663652979, 1663653065);
INSERT INTO `fa_event` VALUES (253, 1, '1875-06-04 00:00:00', '1875-05-01 00:00:00', '俄使索思诺福齐等五人来西北大营，窥探虚实，公接之以礼。俄使提出由俄支援部分军粮。', 1663653010, 1663653010);
INSERT INTO `fa_event` VALUES (254, 1, '1875-08-31 00:00:00', '1875-08-01 00:00:00', '奏以刘锦棠总理行营营务处，率老湘全军西征，刘典帮办陕甘军务。', 1663653030, 1663653030);
INSERT INTO `fa_event` VALUES (255, 1, '1875-03-08 00:00:00', '1875-02-01 00:00:00', '左宗棠接到军机大臣密寄上谕一道。密谕中首先告知左宗棠，有人反对西征，建议撤军，但是没有指明是谁反对，只引述了反对者的理由，同时也告知有人支持西征。着左宗棠体察情形，迅速具奏', 1663653093, 1663653093);
INSERT INTO `fa_event` VALUES (256, 1, '1875-07-03 00:00:00', '1875-06-01 00:00:00', '在兰州成立了火药局', 1663653121, 1663653121);
INSERT INTO `fa_event` VALUES (257, 36, '1876-01-26 00:00:00', '1876-01-01 00:00:00', '率老湘全军进肃州。', 1663653198, 1663653198);
INSERT INTO `fa_event` VALUES (258, 1, '1876-01-26 00:00:00', '1876-01-01 00:00:00', '额尔庆额、冯桂增攻玛纳斯城，失利，冯桂增战死。', 1663653379, 1663653391);
INSERT INTO `fa_event` VALUES (259, 40, '1876-02-25 00:00:00', '1876-02-01 00:00:00', '反对借洋款。诏曰：“左宗棠出师塞外，必须士饱马腾。”准借中外商款及各省协饷，凑足一千万两。', 1663653424, 1663653461);
INSERT INTO `fa_event` VALUES (260, 1, '1876-04-07 00:00:00', '1876-03-13 00:00:00', '左宗棠到达肃州，指挥大军出关。', 1663653474, 1663654136);
INSERT INTO `fa_event` VALUES (261, 36, '1876-04-24 00:00:00', '1876-04-01 00:00:00', '大军继发，中外反对又起。公不为所动，定西征战略，缓进急战，先迟后速，致力于北，而收功于南。', 1663653512, 1663653512);
INSERT INTO `fa_event` VALUES (262, 36, '1876-05-23 00:00:00', '1876-05-01 00:00:00', '湘军进古城。', 1663653539, 1663653590);
INSERT INTO `fa_event` VALUES (263, 36, '1876-07-21 00:00:00', '1876-06-01 00:00:00', '进济木萨。合金顺军进阜康，攻克古牧地，拔乌鲁木齐三城。', 1663653581, 1663653581);
INSERT INTO `fa_event` VALUES (264, 36, '1876-09-18 00:00:00', '1876-08-01 00:00:00', '克玛纳斯城，北路平。英使威妥玛代帕夏乞降，公函陈总理衙门，揭露英诈降阴谋', 1663653615, 1663653632);
INSERT INTO `fa_event` VALUES (265, 42, '1876-11-16 00:00:00', '1876-10-01 00:00:00', '为伊犁将军', 1663653735, 1663653740);
INSERT INTO `fa_event` VALUES (266, 1, '1876-11-16 00:00:00', '1876-10-01 00:00:00', '总理衙门促左宗棠进兵南路。左宗棠仍坚持缓进急战。时大雪封山，请缓至明春雪融，许之。', 1663653773, 1663653773);
INSERT INTO `fa_event` VALUES (267, 43, '1876-03-03 00:00:00', '1876-02-08 00:00:00', '刘典到达兰州', 1663654085, 1663654085);
INSERT INTO `fa_event` VALUES (268, 1, '1877-04-14 00:00:00', '1877-03-01 00:00:00', '左宗棠发出总攻令，命令全军首先攻下南疆门户达坂、吐鲁番、托克逊三角地带，按原定计划分三路并进：刘锦棠率湘军由乌城出发，越岭而南，直攻达坂城；张曜嵩武军先遣孙金彪率五营进驻东西盐池，然后督大军自哈密西进，徐占彪蜀军自巴里坤同日并发。清军总共兵力约 50 营，共 2 万余人，从北、东两方面向达 — 吐 — 托三角地前进。金顺军留守北疆西部地区，监视伊犁俄军动向。', 1663764339, 1663764339);
INSERT INTO `fa_event` VALUES (269, 36, '1877-04-26 00:00:00', '1877-03-13 00:00:00', '收复南疆门户吐鲁番', 1663764376, 1663764376);
INSERT INTO `fa_event` VALUES (270, 36, '1877-08-09 00:00:00', '1877-07-01 00:00:00', '大军向南八城前进时，俄国和土耳其发生了战争。', 1663764402, 1663764402);
INSERT INTO `fa_event` VALUES (271, 36, '1878-01-02 00:00:00', '1877-11-29 00:00:00', '清军克喀什噶尔、叶尔羌、英吉沙尔、和阗。白彦虎、伯克胡里逃入俄境，南八城悉平。七月十七日到十一月二十九日，收复新疆南路，也只用四个半月', 1663764430, 1663764900);
INSERT INTO `fa_event` VALUES (272, 37, '1877-05-13 00:00:00', '1877-04-01 00:00:00', '阿古柏于库尔勒自杀，次子海古拉西窜库车，中途为其兄伯克胡里所杀。伯克与清叛将何步云分守喀什噶尔满、汉城。库伦大臣志崇上言“划地自守之策”，反对继续进军。廷臣亦谓西征耗费过多，公奏驳之。复奏云：“重新疆者，所以保蒙古；保蒙古者，所以卫京师。”并奏请新疆建行省，改郡县。', 1663764467, 1663764494);
INSERT INTO `fa_event` VALUES (273, 1, '1877-07-11 00:00:00', '1877-06-01 00:00:00', '山西、河南大旱，陕西、甘肃也发生灾荒，他捐薪一万两，一些官员在他倡议之下，也纷纷捐款', 1663764522, 1663764522);
INSERT INTO `fa_event` VALUES (274, 36, '1877-09-07 00:00:00', '1877-08-01 00:00:00', '西征军进曲惠，白彦虎掘开都河，漫流百余里，附近城镇水深数尺，农田尽淹，庐舍荡然。', 1663764557, 1663764557);
INSERT INTO `fa_event` VALUES (275, 36, '1877-10-07 00:00:00', '1877-09-01 00:00:00', '刘锦棠军克喀喇沙尔、库尔勒、拜城。白彦虎掠回、维民西逃。刘锦棠军救出难民以十万计。拜城及阿克苏城维民不欲西徙，开城出迎清军。旋又收复乌什，南疆东四城悉平。', 1663764875, 1663764875);
INSERT INTO `fa_event` VALUES (276, 1, '1878-01-03 00:00:00', '1877-12-01 00:00:00', '晋封为二等侯。', 1663764913, 1663764913);
INSERT INTO `fa_event` VALUES (277, 10, '1878-02-04 00:00:00', '1878-01-03 00:00:00', ' 孝威妻子贺夫人去世', 1663765291, 1663765291);
INSERT INTO `fa_event` VALUES (278, 45, '1878-10-26 00:00:00', '1878-10-01 00:00:00', '清廷命崇厚为全权大臣使俄，商议收回伊犁事。公复奏详陈新疆善后方略，新疆应改行省，为长治久安之计', 1663765442, 1663765442);
INSERT INTO `fa_event` VALUES (279, 1, '1878-02-02 00:00:00', '1878-01-01 00:00:00', '上疏言：“新疆改设行省，事关西北全局。”', 1663765463, 1663765463);
INSERT INTO `fa_event` VALUES (280, 43, '1878-05-02 00:00:00', '1878-04-01 00:00:00', '刘典以病请归，奏起杨昌濬继之。', 1663765485, 1663765485);
INSERT INTO `fa_event` VALUES (281, 36, '1878-10-26 00:00:00', '1878-10-01 00:00:00', '八月至十月，白彦虎多次扰边，刘锦棠击破之。', 1663765508, 1663765508);
INSERT INTO `fa_event` VALUES (282, 37, '1879-01-22 00:00:00', '1879-01-01 00:00:00', '俄国唆使阿古柏残部越境来犯', 1663765575, 1663765575);
INSERT INTO `fa_event` VALUES (283, 36, '1879-01-23 00:00:00', '1879-01-02 00:00:00', '刘锦棠等军击退之。', 1663765593, 1663765593);
INSERT INTO `fa_event` VALUES (284, 37, '1879-08-18 00:00:00', '1879-07-01 00:00:00', '又进犯', 1663765611, 1663765611);
INSERT INTO `fa_event` VALUES (285, 36, '1879-09-16 00:00:00', '1879-08-01 00:00:00', '大败犯敌，自是残敌不敢再大举犯边。', 1663765624, 1663765624);
INSERT INTO `fa_event` VALUES (286, 45, '1879-02-21 00:00:00', '1879-02-01 00:00:00', '与俄国订约，丧失伊犁附近大量土地及主权，朝议大哗。左宗棠奏陈利害，提出：“先之以议论，委婉而用机（谈判）；次之以战阵，坚忍而求胜。”决意备战，并拟进驻哈密督战。', 1663765659, 1663765777);
INSERT INTO `fa_event` VALUES (287, 45, '1880-01-12 00:00:00', '1879-12-01 00:00:00', '回京，奉旨交刑部治罪。', 1663765679, 1663765679);
INSERT INTO `fa_event` VALUES (288, 1, '1879-07-19 00:00:00', '1879-06-01 00:00:00', '于兰州设织呢局。', 1663765705, 1663765705);
INSERT INTO `fa_event` VALUES (289, 1, '1879-07-09 00:00:00', '1879-05-20 00:00:00', '酒泉湖建成，向游人开放。', 1663765723, 1663765723);
INSERT INTO `fa_event` VALUES (290, 9, '1880-01-12 00:00:00', '1879-12-01 00:00:00', '由幼子孝同陪同，并携带孝威的两个孤子前来甘肃照顾。那年张夫人已 65 岁，左宗棠认为她来后不能伺候他，反而要他照料，对两个幼孙跋涉远来，更以为不可。张夫人于年底到兰州，左宗棠正在肃州行营', 1663765768, 1663765866);
INSERT INTO `fa_event` VALUES (291, 1, '1879-02-21 00:00:00', '1879-02-01 00:00:00', '原任浙江巡抚杨昌濬罢官在家，应左宗棠奏荐，来兰州接替已故刘典帮办陕甘军务的工作。在他的想象中，西北是一片荒凉景象，大漠冷落，飞沙走石，人迹稀少。当他进入陕甘境后，看到大道两旁杨柳成行，高耸云天，浓荫蔽日，枝头鸟鸣蝉噪，春意盎然，路上行人喧阗，宛若塞外江南，大出他的意外。高兴之余，就吟成一首咏左公柳的诗《恭诵左公西行甘棠》：大将筹边尚未还，湖湘子弟满天山；新栽杨柳三千里，引得春风度玉关。', 1663765833, 1663765833);
INSERT INTO `fa_event` VALUES (292, 9, '1880-02-10 00:00:00', '1880-01-01 00:00:00', '张夫人与孝同来到肃州，总算夫妻儿孙团聚了几个月。而宗棠于四月又离肃州去哈密，张夫人和孝同遂回兰州，不久又回长沙了。', 1663765928, 1663765928);
INSERT INTO `fa_event` VALUES (293, 1, '1880-05-26 00:00:00', '1880-04-18 00:00:00', '率领亲军 1000 余人离开肃州大本营。前往新疆哈密。在队伍中，有几名壮士抬着一口空棺材，随着他的乘舆后面。这一列长长的队伍，旌旗招展，士饱马腾，在荒凉的戈壁滩上，缓缓地迎着苍凉的落日前进。这一幅悲壮的场景，显示了西征军全体将士不获胜利决不生还的决心，也显示了中国人民不可轻侮、中国领土不容侵占的神圣意志。左宗棠的这一勇敢、爱国的行动和决心，感动了全国人民，也震惊了中外。俄扬言将派兵舰来华，封锁辽海，沿海戒备。', 1663765976, 1663766287);
INSERT INTO `fa_event` VALUES (294, 32, '1880-06-21 00:00:00', '1880-05-14 00:00:00', '清朝廷召开紧急会议，讨论伊犁交涉的对策。那时文祥已去世，投降派李鸿章的意见占了上风，连平时主战的工部尚书翁同龢、南洋通商大臣兼两江总督刘坤一等都赞成委曲求全，与俄国妥协。清廷便在十九日发出上谕，决定寄希望于谈判，实则是放弃用武力收回伊犁的计划。俄、英、法、美、德等国趁此对清政府惩办崇厚一事提出抗议，清廷屈从西方压力，减免了崇厚的死刑，改为 “权行监禁”。', 1663766046, 1663766046);
INSERT INTO `fa_event` VALUES (295, 46, '1880-02-10 00:00:00', '1880-01-01 00:00:00', '诏命驻英法大臣曾纪泽赴俄国复议。', 1663766219, 1663766219);
INSERT INTO `fa_event` VALUES (296, 1, '1880-03-11 00:00:00', '1880-02-01 00:00:00', '公复奏以战备支援谈判，定三路收复伊犁之策。', 1663766234, 1663766234);
INSERT INTO `fa_event` VALUES (297, 32, '1880-08-11 00:00:00', '1880-07-06 00:00:00', '清廷准备妥协，诏公回京，“以备朝廷顾问”。公奏荐刘锦棠督办新疆军务，张曜帮办军务。', 1663766312, 1663766427);
INSERT INTO `fa_event` VALUES (298, 1, '1880-09-05 00:00:00', '1880-08-01 00:00:00', '兰州织呢局开工。', 1663766351, 1663766351);
INSERT INTO `fa_event` VALUES (299, 36, '1880-11-08 00:00:00', '1880-10-06 00:00:00', '刘锦棠至哈密接替', 1663766465, 1663766465);
INSERT INTO `fa_event` VALUES (300, 1, '1880-11-14 00:00:00', '1880-10-12 00:00:00', '左宗棠启行入关,入关时，心情黯淡。沿途前所植杨柳，已浓荫如幄，此称“左公柳”。俄国在东北展示兵力，公令王诗正、王德榜即率军入关，赴屯张家口备战。', 1663766518, 1663766518);
INSERT INTO `fa_event` VALUES (301, 1, '1880-12-22 00:00:00', '1880-11-21 00:00:00', '抵兰州，诏杨昌濬护理陕甘总督。', 1663766538, 1663766590);
INSERT INTO `fa_event` VALUES (302, 1, '1881-01-03 00:00:00', '1880-12-04 00:00:00', '从兰州出发回京城，所过士民夹道攀留。由他推荐刘锦棠接任他的职务，授钦差大臣督办新疆军务。', 1663766559, 1663766640);
INSERT INTO `fa_event` VALUES (303, 46, '1880-12-21 00:00:00', '1880-11-20 00:00:00', '在俄京与格尔斯会谈', 1663766621, 1663766621);
INSERT INTO `fa_event` VALUES (304, 36, '1881-01-04 00:00:00', '1880-12-05 00:00:00', '升任新疆钦差大臣', 1663766671, 1663766679);
INSERT INTO `fa_event` VALUES (305, 1, '1880-10-04 00:00:00', '1880-09-01 00:00:00', '禁种罂粟 鸦片销声匿迹\r\n不知从哪儿听到的消息，说他的长孙抽鸦片烟，他勃然大怒，立命家人将孙子拿来，予以处死。只有几位姑奶奶是在左宗棠前唯一可以说话的人，她们拉住父亲苦苦相劝，告知他大孙子并没有抽烟的嗜好，不过偶尔抽一两口玩玩的。宗棠才息怒，命将他禁锢在书房内，如果再犯抽烟的毛病，那就非处死不可了。一个月之后，他没有再抽烟，这才放了他出来。', 1663766728, 1663766728);
INSERT INTO `fa_event` VALUES (306, 46, '1881-02-21 00:00:00', '1881-01-23 00:00:00', '改订的中俄《伊犁条约》终于签署了。从交涉过程来看，俄方急于签约，比清政府更为迫切。如果清政府能始终采纳左宗棠的强硬策略，多坚持一些时间，必然能收回更多的权益。', 1663768410, 1663768410);
INSERT INTO `fa_event` VALUES (307, 1, '1881-02-24 00:00:00', '1881-01-26 00:00:00', '左宗棠抵达京师，次日即陛见皇上、皇太后。', 1663768434, 1663768434);
INSERT INTO `fa_event` VALUES (308, 1, '1881-02-27 00:00:00', '1881-01-29 00:00:00', '太后又召见，命他以大学士入值军机（即任军机大臣），并在总理各国事务衙门行走（外交），兼管理兵部事务，从而参与了清政府中央决策机构，对内政、外交、军事都有发言权，清朝廷对他也真可谓 “优礼有加” 了。', 1663768459, 1663768459);
INSERT INTO `fa_event` VALUES (309, 1, '1881-07-28 00:00:00', '1881-07-03 00:00:00', '他中暑得病，请 10 天假，后来又续假 20 天，到次月（闰七月）十三日，他以病难于很快痊愈为由，正式提出请开去大学士和所有各差使缺。\r\n\r\n', 1663768483, 1663768483);
INSERT INTO `fa_event` VALUES (310, 1, '1881-10-28 00:00:00', '1881-09-06 00:00:00', '他的病假还未满期，朝廷下诏授任两江总督兼充办理南洋通商事务大臣，将他调出京师。两江总督和直隶总督同是地方大员中最重要的位置，又兼南洋通商事务大臣，职位仍然隆重，兼之在地方上还具有实权。', 1663768516, 1663768516);
INSERT INTO `fa_event` VALUES (311, 1, '1881-12-11 00:00:00', '1881-10-20 00:00:00', '左宗棠陛辞出都，眷属也一同回湘。', 1663768535, 1663768535);
INSERT INTO `fa_event` VALUES (312, 1, '1882-01-14 00:00:00', '1881-11-25 00:00:00', '抵达长沙。', 1663768557, 1663768557);
INSERT INTO `fa_event` VALUES (313, 1, '1882-01-20 00:00:00', '1881-12-01 00:00:00', '左宗棠请郭嵩焘兄弟吃饭。郭嵩焘叫郭昆焘准备几样菜肴送去赴宴，自己却推辞不去。', 1663768574, 1663768574);
INSERT INTO `fa_event` VALUES (314, 1, '1882-02-02 00:00:00', '1881-12-14 00:00:00', '他和家属离开长沙，起程前往江宁履任。', 1663768588, 1663768588);
INSERT INTO `fa_event` VALUES (315, 1, '1882-02-18 00:00:00', '1881-12-30 00:00:00', '左宗棠抵达南京，随即接任两江总督，受印视事。', 1663768602, 1663768602);
INSERT INTO `fa_event` VALUES (316, 1, '1881-04-28 00:00:00', '1881-04-01 00:00:00', '议增洋、土烟税捐，以湮其流。赴涿州、天津等地视察水利工程。', 1663768785, 1663768785);
INSERT INTO `fa_event` VALUES (317, 1, '1882-02-18 00:00:00', '1882-01-01 00:00:00', '他率随从出省城阅兵，二月初一日到扬州，初二日道经高邮，视察南运河工程，初五日到清江浦，一方面调阅江北各营，一方面巡视运河堤工和淮河形势，随后沿中运河，历顺清河、张福口、运口等处，视察礼河正坝', 1663768825, 1663768952);
INSERT INTO `fa_event` VALUES (318, 1, '1882-03-19 00:00:00', '1882-02-01 00:00:00', '至扬州、高邮、清江浦等地，视察运河堤工，勘察淮河故道，并调阅江北各营。', 1663768849, 1663768849);
INSERT INTO `fa_event` VALUES (319, 1, '1882-04-12 00:00:00', '1882-02-25 00:00:00', '到南京。通过亲自勘探研究，他对淮河水害有了一定的了解，定出了兴修水利的方案。', 1663768862, 1663768966);
INSERT INTO `fa_event` VALUES (320, 1, '1882-05-26 00:00:00', '1882-04-10 00:00:00', '左宗棠离开南京，前往镇江、常州、苏州、上海等地。此行目的是：一方面检阅江南营伍，另一方面视察上海制造局的兵器生产，积极备战，以对付列强特别是法国迫在眉睫的侵略。\r\n十六日晚他乘坐 “满江红” 船抵达苏州胥门码头，随即上岸到抚台衙门拜会卫静澜巡抚，商讨江苏的治理情况，十七日下了大雨，十八日天晴，到大校场阅兵。二十日离开苏州，“满江红” 船由 “白云” 轮船拖带，经由淀山湖、松江，于二十二日下午 3 时驶入上海黄浦江中，制造局总办刘兴锐派 “江安” 轮船迎接。“满江红” 靠拢上海码头后，地方官员都来迎接拜会，左宗棠只接见了几位重要官员，如上海道、江苏藩司、制造局总办等，其余人均辞谢。码头上备有绿呢大轿，准备迎接他去行馆休憩，也被他辞却，为了避免糜费，他决定留住船上。', 1663768886, 1663768997);
INSERT INTO `fa_event` VALUES (321, 1, '1882-11-11 00:00:00', '1882-10-01 00:00:00', '的身体更差了，记忆力也大不如前，于是上疏请予开缺。 辞职\r\n朝廷没有同意他的请求，温旨慰留，给假三个月养病。', 1663768922, 1663769032);
INSERT INTO `fa_event` VALUES (322, 1, '1882-06-12 00:00:00', '1882-04-27 00:00:00', '起程回南京，完成了第一次海防巡视。', 1663769019, 1663769019);
INSERT INTO `fa_event` VALUES (323, 1, '1882-12-04 00:00:00', '1882-10-24 00:00:00', '左宗棠得了第 12 个孙儿，即孝勋的第二子，宗棠为他取名念恒，字南孙，意思是他在南京总督任内出生的。这是宗棠生前最后得到的一个孙子。', 1663769047, 1663769047);
INSERT INTO `fa_event` VALUES (324, 1, '1883-02-08 00:00:00', '1883-01-01 00:00:00', '他在南京为陶澍和林则徐建立了陶、林二公祠，合祀二位先行者，他写了一副对联：三吴颂遗爱，鲸浪初平，治水行盐，如公皆不朽；卅载接音尘，鸿泥偶踏，湘间邗上，今我复重来。他离南京沿江而下，经里河、下河，视察海岸各盐场，又巡视范堤和清水潭等堤工程进度。二月十八日到朱家山河工地，王德榜军队正在开凿长十余里高数十丈的石脊，遇到困难。左宗棠教他们凿石穿孔，装入火药爆破，解决了施工问题。二十七日又到句容勘验新筑成的圩堤，教筑堤人员沿堤埂种植桑秧防护。二十九日，由二三汊河顺流视察通济门石闸。\r\n\r\n', 1663769128, 1663769346);
INSERT INTO `fa_event` VALUES (325, 1, '1883-03-09 00:00:00', '1883-02-01 00:00:00', '视察朱家山河工程，至句容，赤山湖工竣。', 1663769153, 1663769153);
INSERT INTO `fa_event` VALUES (326, 1, '1883-04-07 00:00:00', '1883-03-01 00:00:00', '法攻越南，破南定。公疏请筹办海防，并创立渔团。', 1663769172, 1663769172);
INSERT INTO `fa_event` VALUES (327, 32, '1883-05-07 00:00:00', '1883-04-01 00:00:00', '诏左宗棠调江南淮楚各军赴前敌抗法，并诏李鸿章赴广东督办越南军务。', 1663769222, 1663769222);
INSERT INTO `fa_event` VALUES (328, 1, '1883-07-04 00:00:00', '1883-06-01 00:00:00', '法攻越南益急。公遣王德榜自永州解济边军，募广勇数千人，并请身赴滇粤督师，清廷止之。', 1663769257, 1663769257);
INSERT INTO `fa_event` VALUES (329, 1, '1883-10-01 00:00:00', '1883-09-01 00:00:00', '出阅渔团，至崇明，还至清江视察运河堤工，议复淮河故道。在途染病。他到江阴、靖江、通州（南通）、海门、崇明、吴淞等地，检阅渔团水勇，水勇们演习泅水、凫水、爬桅、超跃、打靶等，技艺娴熟，“精能过人”，参观者都诧为罕见', 1663769282, 1663769436);
INSERT INTO `fa_event` VALUES (330, 1, '1883-10-31 00:00:00', '1883-10-01 00:00:00', '还江宁。自陈衰病，奏请开缺。诏赏假两个月，命公促王德榜成军出关。公檄王德榜于永州募十营，号“恪靖定边军”，筹军火饷需，助其行。', 1663769300, 1663769300);
INSERT INTO `fa_event` VALUES (331, 1, '1882-12-10 00:00:00', '1882-11-01 00:00:00', '修筑范堤。', 1663769375, 1663769375);
INSERT INTO `fa_event` VALUES (332, 1, '1883-06-05 00:00:00', '1883-05-01 00:00:00', '范堤筑成。秋天发大水，水涨到一丈六尺余。幸而几项水利工程已完成，就依次开放车逻、南关各堤，里下河农田得到保障，是年仍获得丰收。', 1663769405, 1663769405);
INSERT INTO `fa_event` VALUES (333, 1, '1883-12-04 00:00:00', '1883-11-05 00:00:00', '左宗棠正在南京总督署内养病，国内金融界发生了一件虽不算大、影响却不小的事件：胡雪岩开设的阜康银号突然倒闭。', 1663769462, 1663769462);
INSERT INTO `fa_event` VALUES (334, 48, '1884-01-28 00:00:00', '1884-01-01 00:00:00', '王德榜军抵南宁。', 1663769607, 1663769654);
INSERT INTO `fa_event` VALUES (335, 1, '1884-02-01 00:00:00', '1884-01-05 00:00:00', '这时左宗棠的目疾更厉害了，再次奏请开缺，并推荐由曾国荃、杨昌濬、裕禄三人之一取代他。在谕旨尚未批下时，正月初五日正当大家高高兴兴过年的时候，他却抱病又到了清江，研究引淮入故道的工程，二十三日回到扬州，又去崇明、上海等地检阅渔团和海防工事。', 1663769693, 1663769693);
INSERT INTO `fa_event` VALUES (336, 1, '1884-02-27 00:00:00', '1884-02-01 00:00:00', '朱家山河工程落成，二月初二他到朱家山河视察，初三日回到南京，这是他任两江总督的最后一次出巡。', 1663769710, 1663769710);
INSERT INTO `fa_event` VALUES (337, 33, '1884-04-08 00:00:00', '1884-03-13 00:00:00', '奉朝廷之命来到南京，代理两江总督；', 1663769725, 1663769725);
INSERT INTO `fa_event` VALUES (338, 1, '1884-04-09 00:00:00', '1884-03-14 00:00:00', '左宗棠交卸了职务，暂时在家中休养。', 1663769750, 1663769750);
INSERT INTO `fa_event` VALUES (339, 36, '1884-11-18 00:00:00', '1884-10-01 00:00:00', '新疆建行省，刘锦棠任第一任巡抚。', 1663769784, 1663770796);
INSERT INTO `fa_event` VALUES (340, 1, '1884-04-25 00:00:00', '1884-04-01 00:00:00', '左宗棠奉诏赴京入见，他已交卸了两江总督。', 1663769862, 1663769862);
INSERT INTO `fa_event` VALUES (341, 34, '1884-04-25 00:00:00', '1884-04-01 00:00:00', '李鸿章与法使福禄诺在天津签订了《中法简明条约》，约中规定：中国承认法国占领越南，撤回驻在越南的军队，并允许法国商品由云南、广西自由输入内地等。这项条约严重地损害了中越两国人民的利益，又向侵略者打开了中国西南大门。一时弹劾李鸿章的奏章多达 47 封。社会上一些人称他为 “汉奸”。\r\n', 1663769917, 1663769917);
INSERT INTO `fa_event` VALUES (342, 48, '1884-06-01 00:00:00', '1884-05-08 00:00:00', '观音桥之战，法军阵亡 24 人，清军伤亡 300 余人。', 1663769972, 1663769972);
INSERT INTO `fa_event` VALUES (343, 1, '1884-06-13 00:00:00', '1884-05-20 00:00:00', '左宗棠抵达京师,朝廷着他管理神机营，这是卫戍京师的重要任务。主战派的首领是醇亲王奕 ，其他朝内外大臣还有翁同龢、陈宝琛、张之洞、彭玉麟、刘锦棠等。', 1663770003, 1663770017);
INSERT INTO `fa_event` VALUES (344, 1, '1884-07-22 00:00:00', '1884-05-30 00:00:00', '一支法国舰队驶入福建马尾军港。马尾是中国内港，船政大臣何如璋竟不敢阻止，他是李鸿章的亲信，深知李鸿章一意主和，为了避免与法军冲突，不惜放弃国家主权，竟让这支法国舰队停泊在中国的军事要港达一个多月。', 1663770053, 1663770053);
INSERT INTO `fa_event` VALUES (345, 1, '1884-07-22 00:00:00', '1884-06-01 00:00:00', '孤拔率领法舰 13 艘也驶抵台湾海峡。六月十五日，突然向基隆发动进攻，旋即登陆占领基隆炮台。督办台湾防务的福建巡抚刘铭传率部队进行了抵抗，将法军击退。法军在基隆吃了败仗后，法国政府十分恼怒，一方面通知停泊在马尾港的法舰队准备策应，一方面命令驻华代理公使谢满禄向清政府提出最后通牒，并于七月一日下旗离开京师。', 1663770152, 1663770152);
INSERT INTO `fa_event` VALUES (346, 1, '1884-08-23 00:00:00', '1884-07-03 00:00:00', ' 马江之役，福建水师全军覆灭\r\n，法国驻福州领事通告何如璋：“本日对华开战！” 何如璋听到这样重大的消息，却坚守秘密，不做任何准备，还对部将们说：“昨天还得到李相电告，和议大有进步。你们听到的开战消息必系谣传。” 然而马尾港内的法国舰队却毫不客气，立即大炮齐发，只用一个多小时就击沉了港内全部中国船只，包括由 11 艘兵船组成的福建水师和另外 19 艘商船。这支由左宗棠苦心经营的马尾船政局制造的兵船组成的舰队，在毫无准备的情况下仓促应战，因为战机全失，虽然进行了英勇抵抗，船只相继被击沉，福建水师将士阵亡 760 余人。法舰又开炮击毁马尾造船厂，然后退出马尾，遭海岸炮台还击，被击伤数艘。', 1663770209, 1663770209);
INSERT INTO `fa_event` VALUES (347, 32, '1884-08-26 00:00:00', '1884-07-06 00:00:00', '即马江之败三天后，宣布对法开战。朝中的主战派以奕、左宗棠为首，态度很坚决。', 1663770240, 1663770240);
INSERT INTO `fa_event` VALUES (348, 1, '1884-09-04 00:00:00', '1884-07-15 00:00:00', '傍晚，左宗棠来到醇亲王官邸，要求奕同意他亲赴福建前线督师，与法军决一雌雄。奕见他要求很坚决，马上就要去前线，急如星火，就劝他不要着急，少安毋躁。其实，奕同情他，也赞成他去，认为唯有他去可以一拼。奕立即上奏太后。三天后，诏令左宗棠以钦差大臣督办福建军务，命他急速赴福建督师。奕当即写信给总理衙门，叙述当时左宗棠谈话时的情况说：“左相犹如伏波将军（马援）的气概：‘老当益壮，男儿要当死于边野，以马革裹尸还’耳。”', 1663770269, 1663770607);
INSERT INTO `fa_event` VALUES (351, 1, '1884-10-14 00:00:00', '1884-08-26 00:00:00', '抵达南京，停留了十余天，为入闽抗法做了一些布置，调来旧部五千人，随同去福建前线。', 1663770646, 1663770646);
INSERT INTO `fa_event` VALUES (349, 1, '1884-09-14 00:00:00', '1884-07-25 00:00:00', '左宗棠离京的前一天，特地到翁同龢寓所辞行。', 1663770414, 1663770414);
INSERT INTO `fa_event` VALUES (350, 1, '1884-09-15 00:00:00', '1884-07-26 00:00:00', '黎明，左宗棠到内廷告辞，慈禧又召见，温谕慰劳。奕知他即将出都，特在中右门外设筵为他送行。他回到寓所后，各位王公大臣都去送行，他一概辞谢；', 1663770431, 1663770583);
INSERT INTO `fa_event` VALUES (352, 1, '1884-10-31 00:00:00', '1884-09-13 00:00:00', '自江宁由江西入闽。', 1663770671, 1663770766);
INSERT INTO `fa_event` VALUES (353, 1, '1884-12-14 00:00:00', '1884-10-27 00:00:00', '左宗棠抵达福州，全城官员聚集在洪山桥接官亭迎接，士绅和老百姓则在浙绍会馆迎候。\r\n在《中法马江战役之回忆》一书中记载说：“钦差大臣左宗棠进入福州时，威风凛凛，旗帜飘扬，上面大书‘恪靖侯左’字样。队伍两行，个个肩荷洋枪，步伐整齐，后面一人乘肥马，执长鞭，头戴双眼花翎，身穿黄绫马褂，主将左宫保是也。” 自马江战败后，福州官民一夕数惊，风声鹤唳，“一见宫保，无异天神降临，所以敬礼如此。”《申报》还说：“想帷幄运筹，谟独裕，蠢尔法人，当不难灭此朝食也。” 福建人十分信任左宗棠，自他到达后，福州人心大定。\r\n', 1663770811, 1663770875);
INSERT INTO `fa_event` VALUES (354, 1, '1885-02-10 00:00:00', '1884-12-26 00:00:00', '正当除夕临近之际，法国军舰突然聚集在马祖澳，准备趁中国官员欢度新年之际，发动一场突袭。左宗棠和杨昌濬得到消息后，立即同往海防前沿巡视。\r\n二十八日，有几艘法舰由白犬洋开来试探，左宗棠正在长门要塞，即下令开炮轰击，法舰见防备森严，只得灰溜溜地掉头开走了。', 1663770827, 1663770953);
INSERT INTO `fa_event` VALUES (355, 1, '1884-12-15 00:00:00', '1884-10-28 00:00:00', '黎明，左宗棠即去拜会总督、将军、巡抚，布置地方防务，听取他们的汇报。', 1663770914, 1663770914);
INSERT INTO `fa_event` VALUES (356, 1, '1884-12-16 00:00:00', '1884-10-29 00:00:00', '拜谒林文忠公则徐祠。这位昔日抗英的英雄，逝去倏忽已 30 年了；', 1663770928, 1663770928);
INSERT INTO `fa_event` VALUES (357, 1, '1885-02-14 00:00:00', '1884-12-30 00:00:00', '王诗正率部队历经艰险，经澎湖到达台南，投入了保卫台湾的战斗。左宗棠又派了营务处道员陈鸣志潜赴台湾，与刘铭传等商量收复基隆的策略。', 1663770992, 1663770992);
INSERT INTO `fa_event` VALUES (358, 1, '1885-02-15 00:00:00', '1885-01-01 00:00:00', '正月，王诗正军抵台南，与法军战于月眉山。杨岳斌军自泉州渡海至卑南。\r\n法军占领镇南关。', 1663771039, 1663771039);
INSERT INTO `fa_event` VALUES (359, 48, '1885-03-17 00:00:00', '1885-02-01 00:00:00', '王德榜率定边军，会广西提督冯子材，大破法兵于镇南关、谅山。\r\n镇南关 — 谅山大捷,越南人民得知清军大胜，纷纷闻风响应起义。越南官员将北宁等处义民集中起来，成立 “忠义五大团”，打出冯子材军旗号，自愿为清军挑饭、做向导，随军助剿。中国国内人民也都欢欣鼓舞。\r\n当法军战败的消息传到巴黎时，引起了法国全国上下的震动。发动侵略战争的茹费里内阁在法国人民的抗议和反对派的攻击下随之倒台。', 1663771061, 1663771358);
INSERT INTO `fa_event` VALUES (360, 1, '1885-04-15 00:00:00', '1885-03-01 00:00:00', '公密陈：“要盟宜慎，防兵难撤。”反对罢兵、议和。', 1663771096, 1663771096);
INSERT INTO `fa_event` VALUES (361, 34, '1885-06-09 00:00:00', '1885-04-27 00:00:00', '与法国公使签订《中法新约》，承认法国占领越南。战场胜利，却换来屈辱外交。', 1663771110, 1663771655);
INSERT INTO `fa_event` VALUES (362, 1, '1885-06-19 00:00:00', '1885-05-07 00:00:00', '中法条约签订后 10 日，他上疏请求回京复命，并恳求开缺回里治病。朝廷赏假一月，但未准许开缺。', 1663771141, 1663771677);
INSERT INTO `fa_event` VALUES (363, 1, '1885-07-12 00:00:00', '1885-06-01 00:00:00', '奏陈：“台湾孤注大洋，为七省门户，关系全局，请移福建巡抚驻台湾。”', 1663771154, 1663771154);
INSERT INTO `fa_event` VALUES (364, 1, '1885-09-05 00:00:00', '1885-07-27 00:00:00', '台风袭击福州，接着下起了倾盆大雨，左宗棠已处于弥留时刻。他口授遗疏，由儿子在榻前记录下来：此次越南和战，实中国强弱一大关键，臣督师南下，迄未大伸挞伐，张我国威，遗恨平生，不能瞑目！\r\n病榻前儿子和亲人们听见他低声喃喃自语：“哦哦！出队！出队！我还要打。这个天下他们不要，我还要。我从南打到北，从西打到东，我要打，皇上也奈何不得……”\r\n全城百姓一闻宫保噩耗，无不扼腕深嗟，皆谓朝廷失一良将，吾闽亦失一长城。\r\n遗疏云：“此次越南和战，实中国强弱一大关键。臣督师南下，迄未大伸挞伐，张我国威，遗恨平生，不能瞑目！”福州城中巷哭失声，江、浙、关、陇士民闻之，皆奔走悼痛，如失所亲。清廷震悼，追赠太傅，予谥“文襄”，建立专祠。', 1663771179, 1663771731);
INSERT INTO `fa_event` VALUES (365, 1, '1886-11-26 00:00:00', '1886-11-01 00:00:00', '葬于善化（长沙）八都杨梅河柏竹塘山之阳。', 1663771194, 1663771194);
INSERT INTO `fa_event` VALUES (366, 32, '1885-04-07 00:00:00', '1885-02-22 00:00:00', '法人复请议和，慈禧太后立即下诏停战。停战令迅速传到前线，这时中越边境将士士气高昂，中越军民同仇敌忾，正在奋勇追击溃败的法军，突然传来 “停战撤兵” 的谕旨，将士们惊讶万分，人人愤恨不已。据史书记载说，将士们 “拔剑砍地，恨恨连声”。王德榜和冯子材正在追击法军途中，得到谕旨后，虽然气愤万分，但只能服从圣旨，放弃已收复的城池，将军队撤退到广西境内。', 1663771379, 1663771395);
INSERT INTO `fa_event` VALUES (367, 48, '1885-04-15 00:00:00', '1885-03-01 00:00:00', '冯子材和王德榜这些前线将领实在气愤不过，在规定停战那天子刻（零时），打电报给两广总督张之洞说：去岁上谕：议和者诛！请上折诛议和之人。士气可奋，法可除，越可复，后患可免。', 1663771440, 1663771440);
INSERT INTO `fa_event` VALUES (368, 49, '1885-05-01 00:00:00', '1885-03-17 00:00:00', '张之洞打电报给左宗棠，要求他出面为国家主持正义，急图补救，电文说：闻中法详细约款十条，日内即将画押，无非有利于法，有害我华之事。我虽已一再上奏劝阻，但初则反遭切责，后则不予理睬，人微言轻，无术挽救。若再草草画押，后悔曷追？公有回天之力，幸速图之，但勿道洞言。', 1663771615, 1663771615);
INSERT INTO `fa_event` VALUES (369, 1, '1885-07-21 00:00:00', '1885-06-10 00:00:00', '忽然痰涌上来，气喘不已，手足抽搐，昏迷过去。医生赶紧进药急救，经过一个多小时，才苏醒过来。', 1663771692, 1663771692);
INSERT INTO `fa_event` VALUES (370, 32, '1885-10-12 00:00:00', '1885-09-05 00:00:00', '朝廷下令台湾开置行省。又过了若干年，南北铁路干线建成，电讯网络、船炮厂矿陆续发展起来，海防以至全部防务都由专司负责，但那已是遥远的事，他已经来不及见了。', 1663771752, 1663771752);
INSERT INTO `fa_event` VALUES (371, 32, '1885-10-15 00:00:00', '1885-09-08 00:00:00', '朝廷派来的特使、新任福州将军古尼音布（字子清）代行御祭。随后灵柩出发，送回湖南原籍。出殡之日，《申报》记载：“送葬者自督抚、将军、学政、司道各宪之下，均徒步徐行，闽人士感公恩德，一律闭门罢市，且罔不泣下沾襟。自皇华馆至南台，沿路张结素幔，排列香案。绅士及正谊书院肄业生皆在南台中亭路祭。远近观者，如海如山，路为之塞。是非公德泽及人，曷克令人爱慕如此！”', 1663771789, 1663771789);
INSERT INTO `fa_event` VALUES (372, 36, '1894-02-06 00:00:00', '1894-01-01 00:00:00', '中日甲午战起，朝廷想起了刘锦棠，电召入京。他已告归多年了，立即从湘乡乡间起程，不幸刚到县城就病倒了。病重时口齿已经不清楚，还喃喃呼叫：“将士们，保国卫边，奋勇杀敌！” 死时年仅 51 岁，谥号襄勤。', 1663771849, 1663771849);
INSERT INTO `fa_event` VALUES (373, 50, '1907-07-10 00:00:00', '1907-06-01 00:00:00', '福州船政局于光绪三十三年（1907 年）因 “管理不善，经费支绌” 而停办，辛亥革命后，孙中山的国民政府恢复了船政局，改名 “马尾造船所”，但规模不大，建造的船也不多。', 1663771986, 1663771986);
INSERT INTO `fa_event` VALUES (374, 50, '1912-02-18 00:00:00', '1912-01-01 00:00:00', '乘轮由沪抵达罗星塔下的马尾镇，参观了马尾造船所，并向全所员工发表讲话', 1663772001, 1663772001);
INSERT INTO `fa_event` VALUES (375, 28, '1843-06-01 00:00:00', '1843-05-04 00:00:00', '在广东花县莲花塘吸收了第一个拜上帝会员李敬芳，随后少年时代的同学冯云山与族弟洪仁玕参加。并作诗稱“天父天兄太平時，保汝處處有飯喫。耕田首先在根苗，殺盡韃子閻羅妖。”又作“手握乾坤杀伐权，斩邪留正解民悬。手持三尺定山河，太平一统乐如何。”', 1663902350, 1663902350);
INSERT INTO `fa_event` VALUES (376, 28, '1844-06-01 00:00:00', '1844-04-16 00:00:00', '与表亲冯云山、族弟洪仁玕从梁发的《劝世良言》宣传书籍中吸取某些基督教教义，后来自行洗礼。是年，洪秀全和冯云山到广西贵县一带传教，洪不久便返回广东，冯留在广西发展，在当地的信徒日增。', 1663902813, 1663902813);
INSERT INTO `fa_event` VALUES (377, 28, '1829-06-01 00:00:00', '1829-04-30 00:00:00', '到广州参加科举考试，结果落榜了', 1663903117, 1663903117);
INSERT INTO `fa_event` VALUES (378, 28, '1836-06-01 00:00:00', '1836-04-18 00:00:00', '准备了7年，又跑到广州参加科举考试，结果再次落榜。垂头丧气，走在广州街头，看到有人发传单，是宣传基督教的《劝世良言》，要了一份，收藏了起来，但也没时间读', 1663903144, 1663903144);
INSERT INTO `fa_event` VALUES (379, 28, '1837-06-01 00:00:00', '1837-04-28 00:00:00', '第三次跑到广州参加科举考试，不出意外，再次落榜。这下精神上彻底垮了，回到家之后，自暴自弃，染病卧床，期间开始阅读《劝世良言》，认识了什么是基督，什么是上帝，如获至宝', 1663903169, 1663903179);
INSERT INTO `fa_event` VALUES (380, 49, '1841-06-01 00:00:00', '1841-04-12 00:00:00', '开蒙入学,师从何养源\r\n', 1663983879, 1663983879);
